import { Injectable, signal, computed, inject } from '@angular/core';
import { Observable, Subscription } from 'rxjs';
import { ApiService, ApiError } from './api.service';

/**
 * Estado de una petición API
 */
export interface ApiState<T> {
  data: T | null;
  loading: boolean;
  error: ApiError | null;
}

/**
 * Resultado del hook useApi
 */
export interface UseApiResult<T> {
  /** Signal con los datos de la respuesta */
  data: () => T | null;
  /** Signal que indica si está cargando */
  loading: () => boolean;
  /** Signal con el error si existe */
  error: () => ApiError | null;
  /** Signal computed que indica si la petición fue exitosa */
  success: () => boolean;
  /** Signal computed que indica si hay error */
  hasError: () => boolean;
  /** Ejecuta una petición */
  execute: (request: Observable<T>) => Subscription;
  /** Resetea el estado */
  reset: () => void;
  /** Actualiza los datos manualmente */
  setData: (data: T) => void;
}

/**
 * Hook signal-based para consumir APIs con estado reactivo.
 * 
 * @example
 * ```typescript
 * // En tu componente
 * private api = inject(ApiService);
 * private userApi = useApi<User[]>();
 * 
 * // Acceso a signals
 * users = this.userApi.data;
 * loading = this.userApi.loading;
 * error = this.userApi.error;
 * 
 * // Ejecutar petición
 * loadUsers() {
 *   this.userApi.execute(this.api.get('/users'));
 * }
 * 
 * // En template
 * @if (loading()) {
 *   <app-loader></app-loader>
 * } @else if (error()) {
 *   <p>Error: {{ error()?.message }}</p>
 * } @else {
 *   @for (user of users(); track user.id) {
 *     <p>{{ user.name }}</p>
 *   }
 * }
 * ```
 */
export function useApi<T>(initialData: T | null = null): UseApiResult<T> {
  const data = signal<T | null>(initialData);
  const loading = signal<boolean>(false);
  const error = signal<ApiError | null>(null);

  const success = computed(() => data() !== null && !loading() && !error());
  const hasError = computed(() => error() !== null);

  let currentSubscription: Subscription | null = null;

  const execute = (request: Observable<T>): Subscription => {
    // Cancel previous request if exists
    if (currentSubscription) {
      currentSubscription.unsubscribe();
    }

    loading.set(true);
    error.set(null);

    currentSubscription = request.subscribe({
      next: (result) => {
        data.set(result);
        loading.set(false);
      },
      error: (err: ApiError) => {
        error.set(err);
        loading.set(false);
      }
    });

    return currentSubscription;
  };

  const reset = (): void => {
    if (currentSubscription) {
      currentSubscription.unsubscribe();
      currentSubscription = null;
    }
    data.set(initialData);
    loading.set(false);
    error.set(null);
  };

  const setData = (newData: T): void => {
    data.set(newData);
    error.set(null);
  };

  return {
    data: data.asReadonly(),
    loading: loading.asReadonly(),
    error: error.asReadonly(),
    success,
    hasError,
    execute,
    reset,
    setData
  };
}

/**
 * Servicio inyectable alternativo para usar con DI.
 * Útil cuando necesitas compartir estado entre componentes.
 * 
 * @example
 * ```typescript
 * // Crear servicio específico
 * @Injectable({ providedIn: 'root' })
 * export class UsersApiService extends UseApiService<User[]> {
 *   private api = inject(ApiService);
 *   
 *   loadUsers() {
 *     this.execute(this.api.get('/users'));
 *   }
 * }
 * ```
 */
@Injectable()
export class UseApiService<T> {
  private readonly _data = signal<T | null>(null);
  private readonly _loading = signal<boolean>(false);
  private readonly _error = signal<ApiError | null>(null);
  private subscription: Subscription | null = null;

  readonly data = this._data.asReadonly();
  readonly loading = this._loading.asReadonly();
  readonly error = this._error.asReadonly();
  readonly success = computed(() => this._data() !== null && !this._loading() && !this._error());
  readonly hasError = computed(() => this._error() !== null);

  execute(request: Observable<T>): Subscription {
    if (this.subscription) {
      this.subscription.unsubscribe();
    }

    this._loading.set(true);
    this._error.set(null);

    this.subscription = request.subscribe({
      next: (result) => {
        this._data.set(result);
        this._loading.set(false);
      },
      error: (err: ApiError) => {
        this._error.set(err);
        this._loading.set(false);
      }
    });

    return this.subscription;
  }

  reset(): void {
    if (this.subscription) {
      this.subscription.unsubscribe();
      this.subscription = null;
    }
    this._data.set(null);
    this._loading.set(false);
    this._error.set(null);
  }

  setData(data: T): void {
    this._data.set(data);
    this._error.set(null);
  }
}

import { Component, inject, signal, computed, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { Router } from '@angular/router';
import {
  LayoutShellComponent,
  TopbarComponent,
  SidebarComponent,
  SidebarUser,
  PanelComponent,
  RowComponent,
  AvatarComponent,
  TextComponent,
  ButtonComponent,
  ChipComponent,
  SkeletonComponent,
  ThemeSwitcherComponent,
  ApiService,
  useApi
} from '@shared/ui';

/**
 * User info interface
 * @customize Adjust to match your API response
 */
interface User {
  id: string;
  name: string;
  email: string;
  role?: string;
  avatar?: string;
}

/**
 * Dashboard stats interface
 * @customize Adjust to match your API response
 */
interface DashboardStats {
  totalUsers: number;
  activeProjects: number;
  pendingTasks: number;
  revenue: number;
}

/**
 * Menu item interface for sidebar navigation
 */
interface MenuItem {
  id: string;
  label: string;
  icon: string;
  route?: string;
  badge?: number;
  children?: MenuItem[];
}

/**
 * Dashboard Page Blueprint
 * 
 * Features:
 * - LayoutShell with responsive sidebar
 * - Topbar with user menu and notifications
 * - Dynamic sidebar navigation
 * - Stats cards with API integration
 * - Loading states and error handling
 * - Dark mode support
 * 
 * @usage
 * 1. Copy this folder to your project's pages directory
 * 2. Update imports to match your project structure
 * 3. Configure menu items and API endpoints
 * 4. Add route in app.routes.ts with auth guard
 */
@Component({
  selector: 'app-dashboard-page',
  standalone: true,
  imports: [
    CommonModule,
    LayoutShellComponent,
    TopbarComponent,
    SidebarComponent,
    PanelComponent,
    RowComponent,
    AvatarComponent,
    TextComponent,
    ButtonComponent,
    ChipComponent,
    SkeletonComponent,
    ThemeSwitcherComponent
  ],
  templateUrl: './dashboard-page.component.html',
  styleUrl: './dashboard-page.component.css'
})
export class DashboardPageComponent implements OnInit {
  private api = inject(ApiService);
  private router = inject(Router);

  // ============================================
  // CONFIGURATION - Customize these values
  // ============================================

  /** @customize Set your API base URL */
  private readonly API_BASE_URL = 'https://api.example.com/v1';

  /** @customize Token storage key */
  private readonly TOKEN_KEY = 'auth_token';

  // ============================================
  // STATE
  // ============================================

  /** Sidebar visibility state */
  sidebarVisible = signal(true);

  /** Current active menu item */
  activeMenuItem = signal<string>('dashboard');

  /** User info */
  currentUser = signal<User | null>(null);

  /** Dashboard stats API hook */
  statsApi = useApi<DashboardStats>();

  // ============================================
  // MENU CONFIGURATION
  // ============================================

  /** @customize Configure your menu items */
  menuItems: MenuItem[] = [
    {
      id: 'dashboard',
      label: 'Dashboard',
      icon: 'fa-solid fa-chart-pie',
      route: '/dashboard'
    },
    {
      id: 'crud',
      label: 'CRUD',
      icon: 'fa-solid fa-users',
      route: '/crud',
      badge: 5
    },
    {
      id: 'projects',
      label: 'Proyectos',
      icon: 'fa-solid fa-folder-open',
      route: '/dashboard/projects'
    },
    {
      id: 'reports',
      label: 'Reportes',
      icon: 'fa-solid fa-chart-bar',
      route: '/dashboard/reports'
    },
    {
      id: 'settings',
      label: 'Configuración',
      icon: 'fa-solid fa-gear',
      route: '/dashboard/settings'
    }
  ];

  // ============================================
  // COMPUTED
  // ============================================

  /** User initials for avatar */
  userInitials = computed(() => {
    const user = this.currentUser();
    if (!user?.name) return 'U';
    return user.name
      .split(' ')
      .map(n => n[0])
      .join('')
      .toUpperCase()
      .slice(0, 2);
  });

  /** Adapted user for sidebar */
  sidebarUser = computed<SidebarUser | null>(() => {
    const u = this.currentUser();
    if (!u) return null;
    return {
      name: u.name,
      role: u.role || 'User',
      initials: this.userInitials(),
      photo: u.avatar
    };
  });

  // ============================================
  // LIFECYCLE
  // ============================================

  constructor() {
    this.api.setBaseUrl(this.API_BASE_URL);
    this.loadUserFromStorage();
    this.setupAuthToken();
  }

  ngOnInit(): void {
    this.loadDashboardStats();
  }

  // ============================================
  // INITIALIZATION
  // ============================================

  private loadUserFromStorage(): void {
    const userJson = localStorage.getItem('user') || sessionStorage.getItem('user');
    if (userJson) {
      try {
        this.currentUser.set(JSON.parse(userJson));
      } catch {
        console.error('Failed to parse user from storage');
      }
    }
  }

  private setupAuthToken(): void {
    const token = localStorage.getItem(this.TOKEN_KEY) || sessionStorage.getItem(this.TOKEN_KEY);
    if (token) {
      this.api.setAuthToken(token);
    } else {
      // No token, redirect to login
      this.router.navigate(['/login']);
    }
  }

  // ============================================
  // API CALLS
  // ============================================

  loadDashboardStats(): void {
    this.statsApi.execute(
      this.api.get<DashboardStats>('/dashboard/stats')
    );
  }

  // ============================================
  // SIDEBAR ACTIONS
  // ============================================

  toggleSidebar(): void {
    this.sidebarVisible.update(v => !v);
  }

  closeSidebar(): void {
    this.sidebarVisible.set(false);
  }

  onMenuItemClick(item: any): void {
    this.activeMenuItem.set(item.id || item.label);

    if (item.route) {
      this.router.navigate([item.route]);
    }

    // Close sidebar on mobile
    if (window.innerWidth <= 768) {
      this.closeSidebar();
    }
  }

  isMenuItemActive(itemId: string): boolean {
    return this.activeMenuItem() === itemId;
  }

  // ============================================
  // USER ACTIONS
  // ============================================

  onUserAction(action: string): void {
    switch (action) {
      case 'profile':
        this.router.navigate(['/dashboard/profile']);
        break;
      case 'settings':
        this.router.navigate(['/dashboard/settings']);
        break;
      case 'change-password':
        this.router.navigate(['/dashboard/change-password']);
        break;
      default:
        console.log('Unknown action:', action);
    }
  }

  onLogout(): void {
    // Clear stored data
    localStorage.removeItem(this.TOKEN_KEY);
    localStorage.removeItem('user');
    sessionStorage.removeItem(this.TOKEN_KEY);
    sessionStorage.removeItem('user');

    // Clear API auth
    this.api.clearAuthToken();

    // Redirect to login
    this.router.navigate(['/login']);
  }

  // ============================================
  // STATS HELPERS
  // ============================================

  formatNumber(value: number): string {
    if (value >= 1000000) {
      return (value / 1000000).toFixed(1) + 'M';
    }
    if (value >= 1000) {
      return (value / 1000).toFixed(1) + 'K';
    }
    return value.toString();
  }

  formatCurrency(value: number): string {
    return new Intl.NumberFormat('es-PE', {
      style: 'currency',
      currency: 'PEN',
      minimumFractionDigits: 0
    }).format(value);
  }
}

import {
  Component, Input, Output, EventEmitter, signal, HostListener,
  ElementRef, ChangeDetectionStrategy, forwardRef, OnChanges, SimpleChanges, inject
} from '@angular/core';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';

export interface DropdownOption {
  value: string | number;
  label: string;
  icon?: string;
  disabled?: boolean;
}

@Component({
  selector: 'app-dropdown',
  standalone: true,
  changeDetection: ChangeDetectionStrategy.OnPush,
  providers: [{
    provide: NG_VALUE_ACCESSOR,
    useExisting: forwardRef(() => DropdownComponent),
    multi: true
  }],
  template: `
    <div class="dropdown" [class.open]="isOpen()" [class.disabled]="disabled">
      <button type="button"
        class="dropdown-trigger" 
        (click)="toggleDropdown()"
        (keydown.enter)="toggleDropdown()"
        (keydown.space)="toggleDropdown()"
        [attr.aria-expanded]="isOpen()"
        aria-haspopup="listbox"
        [disabled]="disabled"
      >
        <span class="dropdown-value">
          @if (selectedOption()) {
            @if (selectedOption()!.icon) {
              <span class="option-icon">{{ selectedOption()!.icon }}</span>
            }
            {{ selectedOption()!.label }}
          } @else {
            <span class="placeholder">{{ placeholder }}</span>
          }
        </span>
        <span class="dropdown-arrow">
          <svg width="12" height="12" viewBox="0 0 12 12" fill="none">
            <path d="M2 4L6 8L10 4" stroke="currentColor" stroke-width="2" stroke-linecap="round"/>
          </svg>
        </span>
      </button>
      
      @if (isOpen()) {
        <div class="dropdown-menu" role="listbox">
          @for (option of options; track option.value) {
            <button type="button"
              class="dropdown-option"
              [class.selected]="option.value === value"
              [class.disabled]="option.disabled"
              (click)="!option.disabled && selectOption(option)"
              (keydown.enter)="!option.disabled && selectOption(option)"
              (keydown.space)="!option.disabled && selectOption(option)"
              role="option"
              [attr.aria-selected]="option.value === value"
            >
              @if (option.icon) {
                <span class="option-icon">{{ option.icon }}</span>
              }
              {{ option.label }}
              @if (option.value === value) {
                <span class="check-icon">✓</span>
              }
            </button>
          }
        </div>
      }
    </div>
  `,
  styles: [`
    .dropdown {
      position: relative;
      display: inline-block;
      min-width: 180px;
    }

    .dropdown.disabled {
      opacity: 0.6;
      pointer-events: none;
    }

    .dropdown-trigger {
      width: 100%;
      display: flex;
      align-items: center;
      justify-content: space-between;
      padding: 0.625rem 0.875rem;
      background: var(--surface-background);
      border: 1px solid var(--border-color);
      border-radius: 0.5rem;
      font-size: 0.875rem;
      color: var(--text-color);
      cursor: pointer;
      transition: all 150ms ease;
    }

    .dropdown-trigger:hover:not(:disabled) {
      border-color: var(--primary-color);
    }

    .dropdown.open .dropdown-trigger {
      border-color: var(--primary-color);
      box-shadow: var(--focus-ring);
    }

    .dropdown-value {
      display: flex;
      align-items: center;
      gap: 0.5rem;
    }

    .placeholder {
      color: var(--text-color-muted);
    }

    .dropdown-arrow {
      color: var(--text-color-muted);
      transition: transform 200ms ease;
    }

    .dropdown.open .dropdown-arrow {
      transform: rotate(180deg);
    }

    .dropdown-menu {
      position: absolute;
      top: calc(100% + 4px);
      left: 0;
      right: 0;
      background: var(--surface-background);
      border: 1px solid var(--border-color);
      border-radius: 0.5rem;
      box-shadow: var(--shadow-lg);
      z-index: 100;
      max-height: 240px;
      overflow-y: auto;
      animation: dropdownFade 150ms ease;
    }

    .dropdown-option {
      width: 100%;
      display: flex;
      align-items: center;
      gap: 0.5rem;
      padding: 0.625rem 0.875rem;
      background: none;
      border: none;
      font-size: 0.875rem;
      color: var(--text-color);
      cursor: pointer;
      transition: background 100ms ease;
      text-align: left;
    }

    .dropdown-option:hover:not(.disabled) {
      background: var(--surface-elevated);
    }

    .dropdown-option.selected {
      color: var(--primary-color);
      font-weight: 500;
    }

    .dropdown-option.disabled {
      opacity: 0.5;
      cursor: not-allowed;
    }

    .option-icon {
      font-size: 1rem;
    }

    .check-icon {
      margin-left: auto;
      color: var(--primary-color);
    }

    @keyframes dropdownFade {
      from { opacity: 0; transform: translateY(-8px); }
      to { opacity: 1; transform: translateY(0); }
    }

    /* Dark mode - improved */
    :host-context(.dark) .dropdown-trigger,
    :host-context(html.dark) .dropdown-trigger,
    :host-context([data-theme="dark"]) .dropdown-trigger {
      background: #1f2937;
      border-color: #4b5563;
      color: #e5e7eb;
    }

    :host-context(.dark) .dropdown-trigger:hover:not(:disabled),
    :host-context(html.dark) .dropdown-trigger:hover:not(:disabled),
    :host-context([data-theme="dark"]) .dropdown-trigger:hover:not(:disabled) {
      border-color: #bc9abb;
    }

    :host-context(.dark) .dropdown.open .dropdown-trigger,
    :host-context(html.dark) .dropdown.open .dropdown-trigger,
    :host-context([data-theme="dark"]) .dropdown.open .dropdown-trigger {
      border-color: #bc9abb;
      box-shadow: 0 0 0 3px rgba(188, 154, 187, 0.2);
    }

    :host-context(.dark) .placeholder,
    :host-context(html.dark) .placeholder,
    :host-context([data-theme="dark"]) .placeholder {
      color: #6b7280;
    }

    :host-context(.dark) .dropdown-arrow,
    :host-context(html.dark) .dropdown-arrow,
    :host-context([data-theme="dark"]) .dropdown-arrow {
      color: #9ca3af;
    }

    :host-context(.dark) .dropdown-menu,
    :host-context(html.dark) .dropdown-menu,
    :host-context([data-theme="dark"]) .dropdown-menu {
      background: #1f2937;
      border-color: #4b5563;
      box-shadow: 0 10px 25px -5px rgba(0, 0, 0, 0.5), 0 0 20px rgba(188, 154, 187, 0.1);
    }

    :host-context(.dark) .dropdown-option,
    :host-context(html.dark) .dropdown-option,
    :host-context([data-theme="dark"]) .dropdown-option {
      color: #d1d5db;
    }

    :host-context(.dark) .dropdown-option:hover:not(.disabled),
    :host-context(html.dark) .dropdown-option:hover:not(.disabled),
    :host-context([data-theme="dark"]) .dropdown-option:hover:not(.disabled) {
      background: #374151;
      color: #ffffff;
    }

    :host-context(.dark) .dropdown-option.selected,
    :host-context(html.dark) .dropdown-option.selected,
    :host-context([data-theme="dark"]) .dropdown-option.selected {
      color: #d8b4d8;
      background: rgba(188, 154, 187, 0.1);
    }

    :host-context(.dark) .check-icon,
    :host-context(html.dark) .check-icon,
    :host-context([data-theme="dark"]) .check-icon {
      color: #d8b4d8;
      text-shadow: 0 0 10px rgba(188, 154, 187, 0.5);
    }
  `]
})
export class DropdownComponent implements OnChanges, ControlValueAccessor {
  @Input() options: DropdownOption[] = [];
  @Input() value?: string | number;
  @Input() placeholder = 'Seleccionar...';
  @Input() disabled = false;
  @Output() valueChange = new EventEmitter<string | number>();

  isOpen = signal(false);
  selectedOption = signal<DropdownOption | null>(null);

  // ControlValueAccessor
  private onChange: (value: string | number) => void = () => { /* noop */ };
  private onTouched: () => void = () => { /* noop */ };

  private readonly elementRef = inject(ElementRef);

  ngOnChanges(changes: SimpleChanges) {
    if (changes['value'] || changes['options']) {
      this.updateSelectedOption();
    }
  }

  writeValue(value: string | number): void {
    this.value = value;
    this.updateSelectedOption();
  }

  registerOnChange(fn: (value: string | number) => void): void {
    this.onChange = fn;
  }

  registerOnTouched(fn: () => void): void {
    this.onTouched = fn;
  }

  setDisabledState(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }

  private updateSelectedOption() {
    if (this.value !== undefined) {
      const option = this.options.find(o => o.value === this.value);
      this.selectedOption.set(option || null);
    } else {
      this.selectedOption.set(null);
    }
  }

  toggleDropdown() {
    if (!this.disabled) {
      this.isOpen.update(v => !v);
      this.onTouched();
    }
  }

  selectOption(option: DropdownOption) {
    this.value = option.value;
    this.selectedOption.set(option);
    this.valueChange.emit(option.value);
    this.onChange(option.value);
    this.isOpen.set(false);
  }

  @HostListener('document:click', ['$event'])
  onClickOutside(event: Event) {
    if (!this.elementRef.nativeElement.contains(event.target)) {
      this.isOpen.set(false);
    }
  }
}

import { Component, inject, signal, computed, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, ReactiveFormsModule, FormsModule, Validators } from '@angular/forms';
import {
  PanelComponent,
  ButtonComponent,
  InputComponent,
  Select2Component,
  TableComponent,
  TableHeadComponent,
  TableRowComponent,
  TableCellComponent,
  RowComponent,
  TextComponent,
  ChipComponent,
  IconButtonComponent,
  PaginationComponent,
  ModalComponent,
  FloatingInputComponent,
  LoaderComponent,
  ToastComponent,
  ThemeService,
  ApiService,
  useApi
} from '@shared/ui';

/**
 * Generic entity interface
 * @customize Adjust to match your data model
 */
export interface Entity {
  id: string | number;
  name: string;
  email: string;
  status: 'active' | 'inactive' | 'pending';
  role: string;
  createdAt: string;
  [key: string]: unknown;
}

/**
 * Paginated response interface
 * @customize Adjust to match your API response
 */
export interface PaginatedResponse<T> {
  data: T[];
  meta: {
    total: number;
    page: number;
    perPage: number;
    totalPages: number;
  };
}

/**
 * Filter options interface
 */
export interface FilterOptions {
  search: string;
  status: string;
  role: string;
}

/**
 * CRUD Table Blueprint
 * 
 * Features:
 * - Data table with pagination
 * - Search and filter functionality
 * - Create/Edit modal
 * - Delete confirmation
 * - Bulk actions
 * - Loading and error states
 * - Responsive design
 * 
 * @usage
 * 1. Copy this folder to your project
 * 2. Rename Entity interface to your model (User, Product, etc.)
 * 3. Configure API endpoints
 * 4. Adjust table columns
 */
@Component({
  selector: 'app-crud-table',
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    FormsModule,
    PanelComponent,
    ButtonComponent,
    InputComponent,
    FloatingInputComponent,
    Select2Component,
    PaginationComponent,
    ModalComponent,
    ChipComponent,
    LoaderComponent,
    IconButtonComponent,
    TableComponent,
    TableHeadComponent,
    TableRowComponent,
    TableCellComponent,
    RowComponent,
    TextComponent
  ],
  templateUrl: './crud-table.component.html',
  styleUrl: './crud-table.component.css'
})
export class CrudTableComponent implements OnInit {
  private fb = inject(FormBuilder);
  private api = inject(ApiService);

  // ============================================
  // CONFIGURATION - Customize these values
  // ============================================

  /** @customize API endpoint for CRUD operations */
  private readonly ENDPOINT = '/users';

  /** @customize Items per page */
  readonly PAGE_SIZE = 10;

  /** @customize Table title */
  readonly TABLE_TITLE = 'Usuarios';

  /** @customize Entity name (singular) */
  readonly ENTITY_NAME = 'Usuario';

  // ============================================
  // STATE
  // ============================================

  /** List API state */
  listApi = useApi<PaginatedResponse<Entity>>();

  /** Create/Update API state */
  saveApi = useApi<Entity>();

  /** Delete API state */
  deleteApi = useApi<{ success: boolean }>();

  /** Current page */
  currentPage = signal(1);

  /** Search term */
  searchTerm = signal('');

  /** Active filters */
  filters = signal<FilterOptions>({
    search: '',
    status: '',
    role: ''
  });

  /** Selected items for bulk actions */
  selectedItems = signal<Set<string | number>>(new Set());

  /** All items selected flag */
  allSelected = signal(false);

  /** Modal visibility */
  showModal = signal(false);

  /** Delete confirmation modal */
  showDeleteModal = signal(false);

  /** Item to delete */
  itemToDelete = signal<Entity | null>(null);

  /** Edit mode flag */
  isEditMode = signal(false);

  /** Current editing item */
  currentItem = signal<Entity | null>(null);

  // ============================================
  // FILTER OPTIONS
  // ============================================

  /** @customize Status options for filter */
  statusOptions = [
    { value: '', label: 'Todos los estados' },
    { value: 'active', label: 'Activo' },
    { value: 'inactive', label: 'Inactivo' },
    { value: 'pending', label: 'Pendiente' }
  ];

  /** @customize Role options for filter */
  roleOptions = [
    { value: '', label: 'Todos los roles' },
    { value: 'admin', label: 'Administrador' },
    { value: 'user', label: 'Usuario' },
    { value: 'guest', label: 'Invitado' }
  ];

  // ============================================
  // FORM
  // ============================================

  entityForm = this.fb.group({
    name: ['', [Validators.required, Validators.minLength(2)]],
    email: ['', [Validators.required, Validators.email]],
    role: ['user', [Validators.required]],
    status: ['active', [Validators.required]]
  });

  // ============================================
  // COMPUTED
  // ============================================

  /** Current page data */
  items = computed(() => this.listApi.data()?.data ?? []);

  /** Pagination meta */
  meta = computed(() => this.listApi.data()?.meta ?? {
    total: 0,
    page: 1,
    perPage: this.PAGE_SIZE,
    totalPages: 1
  });

  /** Number of selected items */
  selectedCount = computed(() => this.selectedItems().size);

  /** Has selected items */
  hasSelection = computed(() => this.selectedCount() > 0);

  // ============================================
  // LIFECYCLE
  // ============================================

  ngOnInit(): void {
    this.loadData();
  }

  // ============================================
  // DATA LOADING
  // ============================================

  loadData(): void {
    const params: Record<string, string> = {
      page: this.currentPage().toString(),
      perPage: this.PAGE_SIZE.toString()
    };

    const filters = this.filters();
    if (filters.search) params['search'] = filters.search;
    if (filters.status) params['status'] = filters.status;
    if (filters.role) params['role'] = filters.role;

    this.listApi.execute(
      this.api.get<PaginatedResponse<Entity>>(this.ENDPOINT, { params })
    );
  }

  // ============================================
  // PAGINATION
  // ============================================

  onPageChange(page: number): void {
    this.currentPage.set(page);
    this.clearSelection();
    this.loadData();
  }

  // ============================================
  // SEARCH & FILTERS
  // ============================================

  onSearch(event: Event): void {
    const input = event.target as HTMLInputElement;
    this.searchTerm.set(input.value);

    // Debounce search
    clearTimeout((this as any)._searchTimeout);
    (this as any)._searchTimeout = setTimeout(() => {
      this.filters.update(f => ({ ...f, search: input.value }));
      this.currentPage.set(1);
      this.loadData();
    }, 300);
  }

  onStatusFilter(status: string): void {
    this.filters.update(f => ({ ...f, status }));
    this.currentPage.set(1);
    this.loadData();
  }

  onRoleFilter(role: string): void {
    this.filters.update(f => ({ ...f, role }));
    this.currentPage.set(1);
    this.loadData();
  }

  clearFilters(): void {
    this.filters.set({ search: '', status: '', role: '' });
    this.searchTerm.set('');
    this.currentPage.set(1);
    this.loadData();
  }

  hasActiveFilters(): boolean {
    const f = this.filters();
    return !!(f.search || f.status || f.role);
  }

  // ============================================
  // SELECTION
  // ============================================

  toggleSelectAll(): void {
    if (this.allSelected()) {
      this.clearSelection();
    } else {
      const allIds = new Set(this.items().map(item => item.id));
      this.selectedItems.set(allIds);
      this.allSelected.set(true);
    }
  }

  toggleSelect(item: Entity): void {
    const selected = new Set(this.selectedItems());
    if (selected.has(item.id)) {
      selected.delete(item.id);
    } else {
      selected.add(item.id);
    }
    this.selectedItems.set(selected);
    this.allSelected.set(selected.size === this.items().length);
  }

  isSelected(item: Entity): boolean {
    return this.selectedItems().has(item.id);
  }

  clearSelection(): void {
    this.selectedItems.set(new Set());
    this.allSelected.set(false);
  }

  // ============================================
  // CREATE / EDIT
  // ============================================

  openCreateModal(): void {
    this.isEditMode.set(false);
    this.currentItem.set(null);
    this.entityForm.reset({
      name: '',
      email: '',
      role: 'user',
      status: 'active'
    });
    this.showModal.set(true);
  }

  openEditModal(item: Entity): void {
    this.isEditMode.set(true);
    this.currentItem.set(item);
    this.entityForm.patchValue({
      name: item.name,
      email: item.email,
      role: item.role,
      status: item.status
    });
    this.showModal.set(true);
  }

  closeModal(): void {
    this.showModal.set(false);
    this.saveApi.reset();
  }

  onSave(): void {
    if (this.entityForm.invalid) {
      this.entityForm.markAllAsTouched();
      return;
    }

    const data = this.entityForm.value;

    if (this.isEditMode()) {
      const item = this.currentItem();
      if (item) {
        this.saveApi.execute(
          this.api.put<Entity>(`${this.ENDPOINT}/${item.id}`, data)
        );
      }
    } else {
      this.saveApi.execute(
        this.api.post<Entity>(this.ENDPOINT, data)
      );
    }

    // Check for success
    const checkSave = setInterval(() => {
      if (this.saveApi.success()) {
        clearInterval(checkSave);
        this.closeModal();
        this.loadData();
      }
    }, 100);
  }

  // ============================================
  // DELETE
  // ============================================

  openDeleteModal(item: Entity): void {
    this.itemToDelete.set(item);
    this.showDeleteModal.set(true);
  }

  closeDeleteModal(): void {
    this.showDeleteModal.set(false);
    this.itemToDelete.set(null);
    this.deleteApi.reset();
  }

  confirmDelete(): void {
    const item = this.itemToDelete();
    if (!item) return;

    this.deleteApi.execute(
      this.api.delete<{ success: boolean }>(`${this.ENDPOINT}/${item.id}`)
    );

    const checkDelete = setInterval(() => {
      if (this.deleteApi.success()) {
        clearInterval(checkDelete);
        this.closeDeleteModal();
        this.loadData();
      }
    }, 100);
  }

