/**
 * Response from login API endpoint
 * @customize Adjust to match your API response structure
 */
interface LoginResponse {
  token: string;
  refreshToken?: string;
  user: {
    id: string;
    name: string;
    email: string;
    role?: string;
  };
}

/**
 * Response from register API endpoint
 * @customize Adjust to match your API response structure
 */
interface RegisterResponse {
  message: string;
  userId?: string;
}

/**
 * Response from forgot password API endpoint
 */
interface ForgotPasswordResponse {
  message: string;
}

/**
 * Login Page Blueprint
 * 
 * Features:
 * - Login form with email/password
 * - Register form with validation
 * - Forgot password flow
 * - Remember me checkbox
 * - API integration with loading states
 * - Error handling and display
 * 
 * @usage
 * 1. Copy this folder to your project's pages directory
 * 2. Update imports to match your project structure
 * 3. Configure API_BASE_URL to your backend
 * 4. Add route in app.routes.ts
 */
@Component({
  selector: 'app-login-page',
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    AuthLayoutComponent,
    FloatingInputComponent,
    ButtonComponent,
    CheckboxComponent,
    FormErrorComponent,
    PanelComponent,
    TextComponent,
    RowComponent,
    FormRowComponent,
    DividerComponent
  ],
  templateUrl: './login-page.component.html',
  styleUrl: './login-page.component.css'
})
export class LoginPageComponent {
  private fb = inject(FormBuilder);
  private api = inject(ApiService);
  private router = inject(Router);

  // ============================================
  // CONFIGURATION - Customize these values
  // ============================================

  /** @customize Set your API base URL */
  private readonly API_BASE_URL = 'https://api.example.com/v1';

  /** @customize Route to redirect after successful login */
  private readonly REDIRECT_AFTER_LOGIN = '/dashboard';

  /** @customize Token storage key */
  private readonly TOKEN_KEY = 'auth_token';

  // ============================================
  // STATE
  // ============================================

  /** Current active view: 'login' | 'register' | 'forgot' */
  activeView = signal<'login' | 'register' | 'forgot'>('login');

  /** API state hooks */
  loginApi = useApi<LoginResponse>();
  registerApi = useApi<RegisterResponse>();
  forgotApi = useApi<ForgotPasswordResponse>();

  /** Success message for forgot password */
  forgotSuccessMessage = signal<string | null>(null);

  // ============================================
  // FORMS
  // ============================================

  loginForm = this.fb.group({
    email: ['demo@example.com', [Validators.required, Validators.email]],
    password: ['123456', [Validators.required, Validators.minLength(6)]],
    rememberMe: [false]
  });

  registerForm = this.fb.group({
    name: ['', [Validators.required, Validators.minLength(2)]],
    email: ['', [Validators.required, Validators.email]],
    password: ['', [Validators.required, Validators.minLength(8)]],
    confirmPassword: ['', [Validators.required]],
    acceptTerms: [false, [Validators.requiredTrue]]
  });

  forgotForm = this.fb.group({
    email: ['', [Validators.required, Validators.email]]
  });

  // ============================================
  // LIFECYCLE
  // ============================================

  constructor() {
    this.api.setBaseUrl(this.API_BASE_URL);
  }

  // ============================================
  // VIEW SWITCHING
  // ============================================

  showLogin(): void {
    this.activeView.set('login');
    this.resetAllForms();
  }

  showRegister(): void {
    this.activeView.set('register');
    this.resetAllForms();
  }

  showForgot(): void {
    this.activeView.set('forgot');
    this.resetAllForms();
  }

  private resetAllForms(): void {
    this.loginForm.reset();
    this.registerForm.reset();
    this.forgotForm.reset();
    this.loginApi.reset();
    this.registerApi.reset();
    this.forgotApi.reset();
    this.forgotSuccessMessage.set(null);
  }

  // ============================================
  // FORM SUBMISSIONS
  // ============================================

  onLogin(): void {
    if (this.loginForm.invalid) {
      this.loginForm.markAllAsTouched();
      return;
    }

    const { email, password, rememberMe } = this.loginForm.value;

    // MOCK LOGIN FOR DEMO PURPOSE (Enable real API by uncommenting line below)
    this.loginApi.execute(
      new Observable(observer => {
        setTimeout(() => {
          observer.next({
            token: 'mock-jwt-token-123456',
            user: {
              id: '1',
              name: 'Demo User',
              email: email || 'demo@example.com'
            }
          });
          observer.complete();
        }, 1500);
      })
      // this.api.post<LoginResponse>('/auth/login', { email, password })
    );

    // Handle successful login
    // Note: In production, use effect() or subscribe to handle async response
    const checkLogin = setInterval(() => {
      if (this.loginApi.success()) {
        clearInterval(checkLogin);
        const response = this.loginApi.data();
        if (response) {
          this.handleLoginSuccess(response, rememberMe ?? false);
        }
      }
    }, 100);
  }

  onRegister(): void {
    if (this.registerForm.invalid) {
      this.registerForm.markAllAsTouched();
      return;
    }

    const { password, confirmPassword } = this.registerForm.value;

    if (password !== confirmPassword) {
      // Handle password mismatch
      return;
    }

    const { name, email } = this.registerForm.value;

    this.registerApi.execute(
      this.api.post<RegisterResponse>('/auth/register', { name, email, password })
    );
  }

  onForgotPassword(): void {
    if (this.forgotForm.invalid) {
      this.forgotForm.markAllAsTouched();
      return;
    }

    const { email } = this.forgotForm.value;

    this.forgotApi.execute(
      this.api.post<ForgotPasswordResponse>('/auth/forgot-password', { email })
    );

    // Handle success
    const checkForgot = setInterval(() => {
      if (this.forgotApi.success()) {
        clearInterval(checkForgot);
        const response = this.forgotApi.data();
        if (response) {
          this.forgotSuccessMessage.set(response.message || 'Se ha enviado un correo con instrucciones');
        }
      }
    }, 100);
  }

  // ============================================
  // HANDLERS
  // ============================================

  private handleLoginSuccess(response: LoginResponse, rememberMe: boolean): void {
    // Store token
    const storage = rememberMe ? localStorage : sessionStorage;
    storage.setItem(this.TOKEN_KEY, response.token);

    // Set auth header for future requests
    this.api.setAuthToken(response.token);

    // Store user info if needed
    storage.setItem('user', JSON.stringify(response.user));

    // Navigate to dashboard
    this.router.navigate([this.REDIRECT_AFTER_LOGIN]);
  }

  // ============================================
  // FORM FIELD ERRORS
  // ============================================

  getFieldError(form: 'login' | 'register' | 'forgot', fieldName: string): string {
    let field;

    if (form === 'login') {
      field = this.loginForm.get(fieldName);
    } else if (form === 'register') {
      field = this.registerForm.get(fieldName);
    } else {
      field = this.forgotForm.get(fieldName);
    }

    if (!field || !field.touched || !field.errors) {
      return '';
    }

    if (field.errors['required']) {
      return 'Este campo es requerido';
    }
    if (field.errors['email']) {
      return 'Ingresa un email válido';
    }
    if (field.errors['minlength']) {
      const min = field.errors['minlength'].requiredLength;
      return `Mínimo ${min} caracteres`;
    }

    return '';
  }
}

import { Component, inject, signal, ChangeDetectionStrategy, PLATFORM_ID } from '@angular/core';
import { isPlatformBrowser } from '@angular/common';
import { TranslateService, TranslateModule } from '@ngx-translate/core';

/** Available language options */
export interface Language {
  code: string;
  name: string;
  flag: string;
}

/**
 * Language switcher component for changing application language.
 * Persists language preference in localStorage.
 * 
 * @example
 * ```html
 * <app-language-switcher></app-language-switcher>
 * ```
 */
@Component({
  selector: 'app-language-switcher',
  standalone: true,
  imports: [TranslateModule],
  changeDetection: ChangeDetectionStrategy.OnPush,
  template: `
    <div class="language-switcher" [class.open]="isOpen()">
      <button 
        type="button" 
        class="language-btn"
        (click)="toggle()"
        [attr.aria-expanded]="isOpen()"
        aria-haspopup="listbox"
      >
        <span class="language-flag">{{ currentLanguage().flag }}</span>
        <span class="language-code">{{ currentLanguage().code.toUpperCase() }}</span>
        <i class="fa-solid fa-chevron-down chevron"></i>
      </button>
      
      @if (isOpen()) {
        <div class="language-dropdown" role="listbox">
          @for (lang of languages; track lang.code) {
            <button 
              type="button"
              class="language-option"
              [class.active]="lang.code === currentLanguage().code"
              [attr.aria-selected]="lang.code === currentLanguage().code"
              role="option"
              (click)="selectLanguage(lang)"
            >
              <span class="language-flag">{{ lang.flag }}</span>
              <span class="language-name">{{ lang.name }}</span>
            </button>
          }
        </div>
      }
    </div>
  `,
  styles: [`
    .language-switcher {
      position: relative;
      display: inline-block;
    }

    .language-btn {
      display: flex;
      align-items: center;
      gap: 0.375rem;
      padding: 0.5rem 0.75rem;
      background: transparent;
      border: 1px solid var(--border-color);
      border-radius: 0.5rem;
      color: var(--text-color);
      font-size: 0.875rem;
      font-weight: 500;
      cursor: pointer;
      transition: all 150ms ease;
    }

    .language-btn:hover {
      background: var(--hover-background-subtle);
      border-color: var(--primary-color);
    }

    .language-flag {
      font-size: 1rem;
    }

    .language-code {
      font-weight: 600;
    }

    .chevron {
      font-size: 0.625rem;
      color: var(--text-color-muted);
      transition: transform 200ms ease;
    }

    .language-switcher.open .chevron {
      transform: rotate(180deg);
    }

    .language-dropdown {
      position: absolute;
      top: calc(100% + 0.5rem);
      right: 0;
      min-width: 140px;
      background: var(--surface-overlay);
      border: 1px solid var(--border-color);
      border-radius: 0.5rem;
      box-shadow: var(--shadow-lg);
      overflow: hidden;
      z-index: 100;
      animation: dropdown-in 150ms ease;
    }

    @keyframes dropdown-in {
      from { opacity: 0; transform: translateY(-8px); }
      to { opacity: 1; transform: translateY(0); }
    }

    .language-option {
      display: flex;
      align-items: center;
      gap: 0.5rem;
      width: 100%;
      padding: 0.625rem 0.875rem;
      background: transparent;
      border: none;
      color: var(--text-color);
      font-size: 0.875rem;
      text-align: left;
      cursor: pointer;
      transition: background 150ms ease;
    }

    .language-option:hover {
      background: var(--hover-background-subtle);
    }

    .language-option.active {
      background: var(--primary-color-lighter);
      color: var(--primary-color);
      font-weight: 600;
    }

    .language-name {
      flex: 1;
    }

    /* Dark mode */
    :host-context(html.dark) .language-dropdown,
    :host-context([data-theme="dark"]) .language-dropdown {
      background: var(--surface-section);
      border-color: var(--border-color);
    }
  `]
})
export class LanguageSwitcherComponent {
  private readonly translate = inject(TranslateService);
  private readonly platformId = inject(PLATFORM_ID);

  /** Available languages */
  readonly languages: Language[] = [
    { code: 'es', name: 'Español', flag: '' },
    { code: 'en', name: 'English', flag: '' }
  ];

  isOpen = signal(false);
  currentLanguage = signal<Language>(this.languages[0]);

  constructor() {
    this.initLanguage();
  }

  private initLanguage(): void {
    if (isPlatformBrowser(this.platformId)) {
      const saved = localStorage.getItem('app-language');
      if (saved) {
        const lang = this.languages.find(l => l.code === saved);
        if (lang) {
          this.currentLanguage.set(lang);
          this.translate.use(lang.code);
        }
      }
    }
  }

  toggle(): void {
    this.isOpen.update(v => !v);
  }

  selectLanguage(lang: Language): void {
    this.currentLanguage.set(lang);
    this.translate.use(lang.code);
    this.isOpen.set(false);

    if (isPlatformBrowser(this.platformId)) {
      localStorage.setItem('app-language', lang.code);
    }
  }
}

