export class DashboardPageComponent implements OnInit {
  private api = inject(ApiService);
  private router = inject(Router);

  // ============================================
  // CONFIGURATION - Customize these values
  // ============================================

  /** @customize Set your API base URL */
  private readonly API_BASE_URL = 'https://api.example.com/v1';

  /** @customize Token storage key */
  private readonly TOKEN_KEY = 'auth_token';

  // ============================================
  // STATE
  // ============================================

  /** Sidebar visibility state */
  sidebarVisible = signal(true);

  /** Current active menu item */
  activeMenuItem = signal<string>('dashboard');

  /** User info */
  currentUser = signal<User | null>(null);

  /** Dashboard stats API hook */
  statsApi = useApi<DashboardStats>();

  // ============================================
  // MENU CONFIGURATION
  // ============================================

  /** @customize Configure your menu items */
  menuItems: MenuItem[] = [
    {
      id: 'dashboard',
      label: 'Dashboard',
      icon: 'fa-solid fa-chart-pie',
      route: '/dashboard'
    },
    {
      id: 'crud',
      label: 'CRUD',
      icon: 'fa-solid fa-users',
      route: '/crud',
      badge: 5
    },
    {
      id: 'projects',
      label: 'Proyectos',
      icon: 'fa-solid fa-folder-open',
      route: '/dashboard/projects'
    },
    {
      id: 'reports',
      label: 'Reportes',
      icon: 'fa-solid fa-chart-bar',
      route: '/dashboard/reports'
    },
    {
      id: 'settings',
      label: 'Configuración',
      icon: 'fa-solid fa-gear',
      route: '/dashboard/settings'
    }
  ];

  // ============================================
  // COMPUTED
  // ============================================

  /** User initials for avatar */
  userInitials = computed(() => {
    const user = this.currentUser();
    if (!user?.name) return 'U';
    return user.name
      .split(' ')
      .map(n => n[0])
      .join('')
      .toUpperCase()
      .slice(0, 2);
  });

  /** Adapted user for sidebar */
  sidebarUser = computed<SidebarUser | null>(() => {
    const u = this.currentUser();
    if (!u) return null;
    return {
      name: u.name,
      role: u.role || 'User',
      initials: this.userInitials(),
      photo: u.avatar
    };
  });

  // ============================================
  // LIFECYCLE
  // ============================================

  constructor() {
    this.api.setBaseUrl(this.API_BASE_URL);
    this.loadUserFromStorage();
    this.setupAuthToken();
  }

  ngOnInit(): void {
    this.loadDashboardStats();
  }

  // ============================================
  // INITIALIZATION
  // ============================================

  private loadUserFromStorage(): void {
    const userJson = localStorage.getItem('user') || sessionStorage.getItem('user');
    if (userJson) {
      try {
        this.currentUser.set(JSON.parse(userJson));
      } catch {
        console.error('Failed to parse user from storage');
      }
    }
  }

  private setupAuthToken(): void {
    const token = localStorage.getItem(this.TOKEN_KEY) || sessionStorage.getItem(this.TOKEN_KEY);
    if (token) {
      this.api.setAuthToken(token);
    } else {
      // No token, redirect to login
      this.router.navigate(['/login']);
    }
  }

  // ============================================
  // API CALLS
  // ============================================

  loadDashboardStats(): void {
    this.statsApi.execute(
      this.api.get<DashboardStats>('/dashboard/stats')
    );
  }

  // ============================================
  // SIDEBAR ACTIONS
  // ============================================

  toggleSidebar(): void {
    this.sidebarVisible.update(v => !v);
  }

  closeSidebar(): void {
    this.sidebarVisible.set(false);
  }

  onMenuItemClick(item: any): void {
    this.activeMenuItem.set(item.id || item.label);

    if (item.route) {
      this.router.navigate([item.route]);
    }

    // Close sidebar on mobile
    if (window.innerWidth <= 768) {
      this.closeSidebar();
    }
  }

  isMenuItemActive(itemId: string): boolean {
    return this.activeMenuItem() === itemId;
  }

  // ============================================
  // USER ACTIONS
  // ============================================

  onUserAction(action: string): void {
    switch (action) {
      case 'profile':
        this.router.navigate(['/dashboard/profile']);
        break;
      case 'settings':
        this.router.navigate(['/dashboard/settings']);
        break;
      case 'change-password':
        this.router.navigate(['/dashboard/change-password']);
        break;
      default:
        console.log('Unknown action:', action);
    }
  }

  onLogout(): void {
    // Clear stored data
    localStorage.removeItem(this.TOKEN_KEY);
    localStorage.removeItem('user');
    sessionStorage.removeItem(this.TOKEN_KEY);
    sessionStorage.removeItem('user');

    // Clear API auth
    this.api.clearAuthToken();

    // Redirect to login
    this.router.navigate(['/login']);
  }

  // ============================================
  // STATS HELPERS
  // ============================================

  formatNumber(value: number): string {
    if (value >= 1000000) {
      return (value / 1000000).toFixed(1) + 'M';
    }
    if (value >= 1000) {
      return (value / 1000).toFixed(1) + 'K';
    }
    return value.toString();
  }

  formatCurrency(value: number): string {
    return new Intl.NumberFormat('es-PE', {
      style: 'currency',
      currency: 'PEN',
      minimumFractionDigits: 0
    }).format(value);
  }
}

import { Component, inject, signal, computed, OnInit } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, ReactiveFormsModule, FormsModule, Validators } from '@angular/forms';
import {
  PanelComponent,
  ButtonComponent,
  InputComponent,
  Select2Component,
  TableComponent,
  TableHeadComponent,
  TableRowComponent,
  TableCellComponent,
  RowComponent,
  TextComponent,
  ChipComponent,
  IconButtonComponent,
  PaginationComponent,
  ModalComponent,
  FloatingInputComponent,
  LoaderComponent,
  ToastComponent,
  ThemeService,
  ApiService,
  useApi
} from '@shared/ui';

/**
 * Generic entity interface
 * @customize Adjust to match your data model
 */
export interface Entity {
  id: string | number;
  name: string;
  email: string;
  status: 'active' | 'inactive' | 'pending';
  role: string;
  createdAt: string;
  [key: string]: unknown;
}

/**
 * Paginated response interface
 * @customize Adjust to match your API response
 */
export interface PaginatedResponse<T> {
  data: T[];
  meta: {
    total: number;
    page: number;
    perPage: number;
    totalPages: number;
  };
}

/**
 * Filter options interface
 */
export interface FilterOptions {
  search: string;
  status: string;
  role: string;
}

/**
 * CRUD Table Blueprint
 * 
 * Features:
 * - Data table with pagination
 * - Search and filter functionality
 * - Create/Edit modal
 * - Delete confirmation
 * - Bulk actions
 * - Loading and error states
 * - Responsive design
 * 
 * @usage
 * 1. Copy this folder to your project
 * 2. Rename Entity interface to your model (User, Product, etc.)
 * 3. Configure API endpoints
 * 4. Adjust table columns
 */
@Component({
  selector: 'app-crud-table',
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    FormsModule,
    PanelComponent,
    ButtonComponent,
    InputComponent,
    FloatingInputComponent,
    Select2Component,
    PaginationComponent,
    ModalComponent,
    ChipComponent,
    LoaderComponent,
    IconButtonComponent,
    TableComponent,
    TableHeadComponent,
    TableRowComponent,
    TableCellComponent,
    RowComponent,
    TextComponent
  ],
  templateUrl: './crud-table.component.html',
  styleUrl: './crud-table.component.css'
})
export class CrudTableComponent implements OnInit {
  private fb = inject(FormBuilder);
  private api = inject(ApiService);

  // ============================================
  // CONFIGURATION - Customize these values
  // ============================================

  /** @customize API endpoint for CRUD operations */
  private readonly ENDPOINT = '/users';

  /** @customize Items per page */
  readonly PAGE_SIZE = 10;

  /** @customize Table title */
  readonly TABLE_TITLE = 'Usuarios';

  /** @customize Entity name (singular) */
  readonly ENTITY_NAME = 'Usuario';

  // ============================================
  // STATE
  // ============================================

  /** List API state */
  listApi = useApi<PaginatedResponse<Entity>>();

  /** Create/Update API state */
  saveApi = useApi<Entity>();

  /** Delete API state */
  deleteApi = useApi<{ success: boolean }>();

  /** Current page */
  currentPage = signal(1);

  /** Search term */
  searchTerm = signal('');

  /** Active filters */
  filters = signal<FilterOptions>({
    search: '',
    status: '',
    role: ''
  });

  /** Selected items for bulk actions */
  selectedItems = signal<Set<string | number>>(new Set());

  /** All items selected flag */
  allSelected = signal(false);

  /** Modal visibility */
  showModal = signal(false);

  /** Delete confirmation modal */
  showDeleteModal = signal(false);

  /** Item to delete */
  itemToDelete = signal<Entity | null>(null);

  /** Edit mode flag */
  isEditMode = signal(false);

  /** Current editing item */
  currentItem = signal<Entity | null>(null);

  // ============================================
  // FILTER OPTIONS
  // ============================================

  /** @customize Status options for filter */
  statusOptions = [
    { value: '', label: 'Todos los estados' },
    { value: 'active', label: 'Activo' },
    { value: 'inactive', label: 'Inactivo' },
    { value: 'pending', label: 'Pendiente' }
  ];

  /** @customize Role options for filter */
  roleOptions = [
    { value: '', label: 'Todos los roles' },
    { value: 'admin', label: 'Administrador' },
    { value: 'user', label: 'Usuario' },
    { value: 'guest', label: 'Invitado' }
  ];

  // ============================================
  // FORM
  // ============================================

  entityForm = this.fb.group({
    name: ['', [Validators.required, Validators.minLength(2)]],
    email: ['', [Validators.required, Validators.email]],
    role: ['user', [Validators.required]],
    status: ['active', [Validators.required]]
  });

  // ============================================
  // COMPUTED
  // ============================================

  /** Current page data */
  items = computed(() => this.listApi.data()?.data ?? []);

  /** Pagination meta */
  meta = computed(() => this.listApi.data()?.meta ?? {
    total: 0,
    page: 1,
    perPage: this.PAGE_SIZE,
    totalPages: 1
  });

  /** Number of selected items */
  selectedCount = computed(() => this.selectedItems().size);

  /** Has selected items */
  hasSelection = computed(() => this.selectedCount() > 0);

  // ============================================
  // LIFECYCLE
  // ============================================

  ngOnInit(): void {
    this.loadData();
  }

  // ============================================
  // DATA LOADING
  // ============================================

  loadData(): void {
    const params: Record<string, string> = {
      page: this.currentPage().toString(),
      perPage: this.PAGE_SIZE.toString()
    };

    const filters = this.filters();
    if (filters.search) params['search'] = filters.search;
    if (filters.status) params['status'] = filters.status;
    if (filters.role) params['role'] = filters.role;

    this.listApi.execute(
      this.api.get<PaginatedResponse<Entity>>(this.ENDPOINT, { params })
    );
  }

  // ============================================
  // PAGINATION
  // ============================================

  onPageChange(page: number): void {
    this.currentPage.set(page);
    this.clearSelection();
    this.loadData();
  }

  // ============================================
  // SEARCH & FILTERS
  // ============================================

  onSearch(event: Event): void {
    const input = event.target as HTMLInputElement;
    this.searchTerm.set(input.value);

    // Debounce search
    clearTimeout((this as any)._searchTimeout);
    (this as any)._searchTimeout = setTimeout(() => {
      this.filters.update(f => ({ ...f, search: input.value }));
      this.currentPage.set(1);
      this.loadData();
    }, 300);
  }

  onStatusFilter(status: string): void {
    this.filters.update(f => ({ ...f, status }));
    this.currentPage.set(1);
    this.loadData();
  }

  onRoleFilter(role: string): void {
    this.filters.update(f => ({ ...f, role }));
    this.currentPage.set(1);
    this.loadData();
  }

  clearFilters(): void {
    this.filters.set({ search: '', status: '', role: '' });
    this.searchTerm.set('');
    this.currentPage.set(1);
    this.loadData();
  }

  hasActiveFilters(): boolean {
    const f = this.filters();
    return !!(f.search || f.status || f.role);
  }

  // ============================================
  // SELECTION
  // ============================================

  toggleSelectAll(): void {
    if (this.allSelected()) {
      this.clearSelection();
    } else {
      const allIds = new Set(this.items().map(item => item.id));
      this.selectedItems.set(allIds);
      this.allSelected.set(true);
    }
  }

  toggleSelect(item: Entity): void {
    const selected = new Set(this.selectedItems());
    if (selected.has(item.id)) {
      selected.delete(item.id);
    } else {
      selected.add(item.id);
    }
    this.selectedItems.set(selected);
    this.allSelected.set(selected.size === this.items().length);
  }

  isSelected(item: Entity): boolean {
    return this.selectedItems().has(item.id);
  }

  clearSelection(): void {
    this.selectedItems.set(new Set());
    this.allSelected.set(false);
  }

  // ============================================
  // CREATE / EDIT
  // ============================================

  openCreateModal(): void {
    this.isEditMode.set(false);
    this.currentItem.set(null);
    this.entityForm.reset({
      name: '',
      email: '',
      role: 'user',
      status: 'active'
    });
    this.showModal.set(true);
  }

  openEditModal(item: Entity): void {
    this.isEditMode.set(true);
    this.currentItem.set(item);
    this.entityForm.patchValue({
      name: item.name,
      email: item.email,
      role: item.role,
      status: item.status
    });
    this.showModal.set(true);
  }

  closeModal(): void {
    this.showModal.set(false);
    this.saveApi.reset();
  }

  onSave(): void {
    if (this.entityForm.invalid) {
      this.entityForm.markAllAsTouched();
      return;
    }

    const data = this.entityForm.value;

    if (this.isEditMode()) {
      const item = this.currentItem();
      if (item) {
        this.saveApi.execute(
          this.api.put<Entity>(`${this.ENDPOINT}/${item.id}`, data)
        );
      }
    } else {
      this.saveApi.execute(
        this.api.post<Entity>(this.ENDPOINT, data)
      );
    }

    // Check for success
    const checkSave = setInterval(() => {
      if (this.saveApi.success()) {
        clearInterval(checkSave);
        this.closeModal();
        this.loadData();
      }
    }, 100);
  }

  // ============================================
  // DELETE
  // ============================================

  openDeleteModal(item: Entity): void {
    this.itemToDelete.set(item);
    this.showDeleteModal.set(true);
  }

  closeDeleteModal(): void {
    this.showDeleteModal.set(false);
    this.itemToDelete.set(null);
    this.deleteApi.reset();
  }

  confirmDelete(): void {
    const item = this.itemToDelete();
    if (!item) return;

    this.deleteApi.execute(
      this.api.delete<{ success: boolean }>(`${this.ENDPOINT}/${item.id}`)
    );

    const checkDelete = setInterval(() => {
      if (this.deleteApi.success()) {
        clearInterval(checkDelete);
        this.closeDeleteModal();
        this.loadData();
      }
    }, 100);
  }

  // ============================================
  // BULK ACTIONS
  // ============================================

  bulkDelete(): void {
    const ids = Array.from(this.selectedItems());
    if (ids.length === 0) return;

    // @customize Implement bulk delete API call
    console.log('Bulk delete:', ids);
    this.clearSelection();
  }

  bulkExport(): void {
    const ids = Array.from(this.selectedItems());
    console.log('Export:', ids.length > 0 ? ids : 'all');
    // @customize Implement export functionality
  }

  // ============================================
  // HELPERS
  // ============================================

  getStatusVariant(status: string): 'success' | 'warning' | 'error' | 'default' {
    switch (status) {
      case 'active': return 'success';
      case 'pending': return 'warning';
      case 'inactive': return 'error';
      default: return 'default';
    }
  }

  getStatusLabel(status: string): string {
    switch (status) {
      case 'active': return 'Activo';
      case 'pending': return 'Pendiente';
      case 'inactive': return 'Inactivo';
      default: return status;
    }
  }

  formatDate(dateString: string): string {
    return new Date(dateString).toLocaleDateString('es-ES', {
      year: 'numeric',
      month: 'short',
      day: 'numeric'
    });
  }

  getFieldError(fieldName: string): string {
    const field = this.entityForm.get(fieldName);
    if (!field || !field.touched || !field.errors) return '';

    if (field.errors['required']) return 'Campo requerido';
    if (field.errors['email']) return 'Email inválido';
    if (field.errors['minlength']) {
      return `Mínimo ${field.errors['minlength'].requiredLength} caracteres`;
    }
    return '';
  }
}

import { Component, inject, signal } from '@angular/core';
import { CommonModule } from '@angular/common';
import { FormBuilder, ReactiveFormsModule, Validators } from '@angular/forms';
import { Router } from '@angular/router';
import { Observable } from 'rxjs';
import {
  AuthLayoutComponent,
  FloatingInputComponent,
  ButtonComponent,
  CheckboxComponent,
  ApiService,
  useApi,
  FormErrorComponent,
  PanelComponent,
  TextComponent,
  RowComponent,
  FormRowComponent,
  DividerComponent
} from '@shared/ui';

/**
 * Response from login API endpoint
 * @customize Adjust to match your API response structure
 */
interface LoginResponse {
  token: string;
  refreshToken?: string;
  user: {
    id: string;
    name: string;
    email: string;
    role?: string;
  };
}

/**
 * Response from register API endpoint
 * @customize Adjust to match your API response structure
 */
interface RegisterResponse {
  message: string;
  userId?: string;
}

/**
 * Response from forgot password API endpoint
 */
interface ForgotPasswordResponse {
  message: string;
}

/**
 * Login Page Blueprint
 * 
 * Features:
 * - Login form with email/password
 * - Register form with validation
 * - Forgot password flow
 * - Remember me checkbox
 * - API integration with loading states
 * - Error handling and display
 * 
 * @usage
 * 1. Copy this folder to your project's pages directory
 * 2. Update imports to match your project structure
 * 3. Configure API_BASE_URL to your backend
 * 4. Add route in app.routes.ts
 */
@Component({
  selector: 'app-login-page',
  standalone: true,
  imports: [
    CommonModule,
    ReactiveFormsModule,
    AuthLayoutComponent,
    FloatingInputComponent,
    ButtonComponent,
    CheckboxComponent,
    FormErrorComponent,
    PanelComponent,
    TextComponent,
    RowComponent,
    FormRowComponent,
    DividerComponent
  ],
  templateUrl: './login-page.component.html',
  styleUrl: './login-page.component.css'
})
export class LoginPageComponent {
  private fb = inject(FormBuilder);
  private api = inject(ApiService);
  private router = inject(Router);

  // ============================================
  // CONFIGURATION - Customize these values
  // ============================================

  /** @customize Set your API base URL */
  private readonly API_BASE_URL = 'https://api.example.com/v1';

  /** @customize Route to redirect after successful login */
  private readonly REDIRECT_AFTER_LOGIN = '/dashboard';

  /** @customize Token storage key */
  private readonly TOKEN_KEY = 'auth_token';

  // ============================================
  // STATE
  // ============================================

  /** Current active view: 'login' | 'register' | 'forgot' */
  activeView = signal<'login' | 'register' | 'forgot'>('login');

  /** API state hooks */
  loginApi = useApi<LoginResponse>();
  registerApi = useApi<RegisterResponse>();
  forgotApi = useApi<ForgotPasswordResponse>();

  /** Success message for forgot password */
  forgotSuccessMessage = signal<string | null>(null);

  // ============================================
  // FORMS
  // ============================================

  loginForm = this.fb.group({
    email: ['demo@example.com', [Validators.required, Validators.email]],
    password: ['123456', [Validators.required, Validators.minLength(6)]],
    rememberMe: [false]
  });

  registerForm = this.fb.group({
    name: ['', [Validators.required, Validators.minLength(2)]],
    email: ['', [Validators.required, Validators.email]],
    password: ['', [Validators.required, Validators.minLength(8)]],
    confirmPassword: ['', [Validators.required]],
    acceptTerms: [false, [Validators.requiredTrue]]
  });

  forgotForm = this.fb.group({
    email: ['', [Validators.required, Validators.email]]
  });

  // ============================================
  // LIFECYCLE
  // ============================================

  constructor() {
    this.api.setBaseUrl(this.API_BASE_URL);
  }

  // ============================================
  // VIEW SWITCHING
  // ============================================

  showLogin(): void {
    this.activeView.set('login');
    this.resetAllForms();
  }

  showRegister(): void {
    this.activeView.set('register');
    this.resetAllForms();
  }

  showForgot(): void {
    this.activeView.set('forgot');
    this.resetAllForms();
  }

  private resetAllForms(): void {
    this.loginForm.reset();
    this.registerForm.reset();
    this.forgotForm.reset();
    this.loginApi.reset();
    this.registerApi.reset();
    this.forgotApi.reset();
    this.forgotSuccessMessage.set(null);
  }

  // ============================================
  // FORM SUBMISSIONS
  // ============================================

  onLogin(): void {
    if (this.loginForm.invalid) {
      this.loginForm.markAllAsTouched();
      return;
    }

    const { email, password, rememberMe } = this.loginForm.value;

    // MOCK LOGIN FOR DEMO PURPOSE (Enable real API by uncommenting line below)
    this.loginApi.execute(
      new Observable(observer => {
        setTimeout(() => {
          observer.next({
            token: 'mock-jwt-token-123456',
            user: {
              id: '1',
              name: 'Demo User',
              email: email || 'demo@example.com'
            }
          });
          observer.complete();
        }, 1500);
      })
      // this.api.post<LoginResponse>('/auth/login', { email, password })
    );

    // Handle successful login
    // Note: In production, use effect() or subscribe to handle async response
    const checkLogin = setInterval(() => {
      if (this.loginApi.success()) {
        clearInterval(checkLogin);
        const response = this.loginApi.data();
        if (response) {
          this.handleLoginSuccess(response, rememberMe ?? false);
        }
      }
    }, 100);
  }

  onRegister(): void {
    if (this.registerForm.invalid) {
      this.registerForm.markAllAsTouched();
      return;
    }

    const { password, confirmPassword } = this.registerForm.value;

    if (password !== confirmPassword) {
      // Handle password mismatch
      return;
    }

    const { name, email } = this.registerForm.value;

    this.registerApi.execute(
      this.api.post<RegisterResponse>('/auth/register', { name, email, password })
    );
  }

  onForgotPassword(): void {
    if (this.forgotForm.invalid) {
      this.forgotForm.markAllAsTouched();
      return;
    }

    const { email } = this.forgotForm.value;

    this.forgotApi.execute(
      this.api.post<ForgotPasswordResponse>('/auth/forgot-password', { email })
    );

    // Handle success
    const checkForgot = setInterval(() => {
      if (this.forgotApi.success()) {
        clearInterval(checkForgot);
        const response = this.forgotApi.data();
        if (response) {
          this.forgotSuccessMessage.set(response.message || 'Se ha enviado un correo con instrucciones');
        }
      }
    }, 100);
  }

  // ============================================
  // HANDLERS
  // ============================================

  private handleLoginSuccess(response: LoginResponse, rememberMe: boolean): void {
    // Store token
    const storage = rememberMe ? localStorage : sessionStorage;
    storage.setItem(this.TOKEN_KEY, response.token);

    // Set auth header for future requests
    this.api.setAuthToken(response.token);

    // Store user info if needed
    storage.setItem('user', JSON.stringify(response.user));

    // Navigate to dashboard
    this.router.navigate([this.REDIRECT_AFTER_LOGIN]);
  }

  // ============================================
  // FORM FIELD ERRORS
  // ============================================

  getFieldError(form: 'login' | 'register' | 'forgot', fieldName: string): string {
    let field;

    if (form === 'login') {
      field = this.loginForm.get(fieldName);
    } else if (form === 'register') {
      field = this.registerForm.get(fieldName);
    } else {
      field = this.forgotForm.get(fieldName);
    }

    if (!field || !field.touched || !field.errors) {
      return '';
    }

    if (field.errors['required']) {
      return 'Este campo es requerido';
    }
    if (field.errors['email']) {
      return 'Ingresa un email válido';
    }
    if (field.errors['minlength']) {
      const min = field.errors['minlength'].requiredLength;
      return `Mínimo ${min} caracteres`;
    }

    return '';
  }
}

import {
  Component, Input, Output, EventEmitter, signal, HostListener,
  ElementRef, forwardRef, inject, ChangeDetectionStrategy
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { ControlValueAccessor, NG_VALUE_ACCESSOR, FormsModule } from '@angular/forms';
import { TranslateModule } from '@ngx-translate/core';

export interface Select2Option {
  value: string | number;
  label: string;
  icon?: string;
  disabled?: boolean;
}

@Component({
  // Standalone component for Select2 dropdown
  selector: 'app-select2',
  standalone: true,
  imports: [CommonModule, FormsModule, TranslateModule],
  changeDetection: ChangeDetectionStrategy.OnPush,
  providers: [{
    provide: NG_VALUE_ACCESSOR,
    useExisting: forwardRef(() => Select2Component),
    multi: true
  }],
  template: `
    <div 
      class="select2-wrapper"
      [class.open]="isOpen()"
      [class.disabled]="disabled"
      [class.focused]="isOpen()"
      [class.has-value]="hasValue()"
      [class.multiple]="multiple"
      [class.has-label]="label"
      [style.width]="width || null"
    >
      <div class="select2-trigger" (click)="toggleDropdown()">
        @if (label) {
          <label class="floating-label">{{ label }}</label>
        }
        <!-- Single value display -->
        @if (!multiple) {
          <span class="select2-value">
            @if (selectedOption()) {
              @if (selectedOption()!.icon) {
                <span class="option-icon">{{ selectedOption()!.icon }}</span>
              }
              {{ selectedOption()!.label | translate }}
            } @else if (!label) {
              <span class="placeholder">{{ placeholder | translate }}</span>
            }
          </span>
        }
        
        <!-- Multiple values as tags -->
        @if (multiple) {
          <div class="select2-tags">
            @for (opt of selectedOptions(); track opt.value) {
              <span class="select2-tag">
                {{ opt.label | translate }}
                <button type="button" class="tag-remove" (click)="removeTag(opt, $event)">×</button>
              </span>
            }
            @if (selectedOptions().length === 0 && !label) {
              <span class="placeholder">{{ placeholder | translate }}</span>
            }
          </div>
        }
        
        <span class="select2-arrow">
          <svg width="12" height="12" viewBox="0 0 12 12" fill="none">
            <path d="M2 4L6 8L10 4" stroke="currentColor" stroke-width="2" stroke-linecap="round"/>
          </svg>
        </span>
      </div>

      @if (isOpen()) {
        <div class="select2-dropdown">
          <!-- Search box -->
          @if (searchable) {
            <div class="select2-search">
              <input 
                type="text" 
                class="search-input" 
                placeholder="Buscar..."
                [ngModel]="searchTerm()"
                (ngModelChange)="searchTerm.set($event)"
                (click)="$event.stopPropagation()"
              />
              <span class="search-icon">🔍</span>
            </div>
          }
          
          <!-- Options list -->
          <div class="select2-options">
            @for (option of filteredOptions(); track option.value) {
              <div 
                class="select2-option"
                [class.selected]="isSelected(option)"
                [class.disabled]="option.disabled"
                (click)="!option.disabled && selectOption(option)"
              >
                @if (option.icon) {
                  <span class="option-icon">{{ option.icon }}</span>
                }
                <span class="option-label">{{ option.label | translate }}</span>
                @if (isSelected(option)) {
                  <span class="check-icon">✓</span>
                }
              </div>
            } @empty {
              <div class="select2-no-results">No hay resultados</div>
            }
          </div>
        </div>
      }
    </div>
  `,
  styles: [`
    :host {
      display: block;
      width: 100%;
    }

    .select2-wrapper {
      position: relative;
      width: 100%;
      min-width: 15rem; /* 240px minimum */
    }

    .select2-wrapper.disabled {
      opacity: 0.6;
      pointer-events: none;
    }

    .select2-wrapper.has-label {
      margin-top: 0.75rem;
    }

    /* === TRIGGER === */
    .select2-trigger {
      position: relative;
      display: flex;
      align-items: center;
      height: var(--control-height);
      padding: 0.25rem 0.875rem;
      padding-right: 2.75rem;
      background: var(--input-bg);
      border: 1px solid var(--input-border);
      border-radius: 0.5rem;
      cursor: pointer;
      transition: all 200ms ease;
      font-size: 0.875rem;
      box-sizing: border-box; 
    }

    .select2-wrapper.has-label .select2-trigger {
      padding: 0.25rem 0.875rem; /* Keep centered, remove top padding shift */
    }

    /* === FLOATING LABEL === */
    .floating-label {
      position: absolute;
      left: 0.875rem;
      top: 50%;
      transform: translateY(-50%);
      font-size: 0.875rem;
      color: var(--input-placeholder);
      pointer-events: none;
      transition: all 200ms cubic-bezier(0.4, 0, 0.2, 1);
      background: var(--input-bg);
      padding: 0 0.5rem;
      white-space: nowrap;
    }

    .select2-wrapper.focused .floating-label,
    .select2-wrapper.has-value .floating-label {
      top: -0.625rem;
      transform: translateY(0);
      font-size: 0.8125rem;
      font-weight: 500;
      color: var(--primary-color);
    }

    /* === MULTI-SELECT FIX === */
    .select2-wrapper.multiple .select2-trigger {
      min-height: var(--control-height); /* grows with content */
      height: auto;
      padding: 0.25rem 0.875rem;
      padding-right: 2.75rem;
      align-items: center; /* Center placeholder and tags vertically */
    }

    .select2-wrapper.focused .select2-trigger,
    .select2-trigger:hover {
      border-color: var(--input-border-focus);
    }

    .select2-wrapper.focused .select2-trigger {
      box-shadow: 0 0 0 3px var(--hover-background);
    }

    .select2-value {
      display: flex;
      align-items: center;
      gap: 0.5rem;
      flex: 1;
      font-size: 0.875rem;
      color: var(--input-text);
      line-height: normal; /* Prevent height expansion */
    }

    .placeholder {
      color: var(--input-placeholder);
    }

    .select2-arrow {
      position: absolute;
      right: 0.75rem;
      top: 50%;
      transform: translateY(-50%);
      color: var(--text-color-secondary);
      transition: transform 200ms ease;
    }

    .select2-wrapper.open .select2-arrow {
      transform: translateY(-50%) rotate(180deg);
    }

    /* === TAGS (Multiple) === */
    .select2-tags {
      display: flex;
      flex-wrap: wrap;
      gap: 0.375rem;
      flex: 1;
      padding: 0.125rem 0;
    }

    .select2-tag {
      display: inline-flex;
      align-items: center;
      gap: 0.25rem;
      padding: 0.25rem 0.5rem;
      background: var(--primary-color-lighter);
      color: var(--primary-color);
      border-radius: 9999px;
      font-size: 0.8125rem;
      font-weight: 500;
    }

    .tag-remove {
      display: flex;
      align-items: center;
      justify-content: center;
      width: 1rem;
      height: 1rem;
      padding: 0;
      background: none;
      border: none;
      border-radius: 50%;
      font-size: 0.875rem;
      color: var(--primary-color);
      cursor: pointer;
      transition: all 150ms ease;
    }

    .tag-remove:hover {
      background: var(--primary-color-lighter);
    }

    /* === DROPDOWN === */
    /* === DROPDOWN === */
    .select2-dropdown {
      position: absolute;
      top: calc(100% + 4px);
      left: 0;
      right: 0;
      background: var(--dropdown-bg);
      border: 1px solid var(--dropdown-border);
      border-radius: 0.5rem;
      box-shadow: var(--dropdown-shadow);
      z-index: 10000;
      animation: dropdownSlide 200ms ease;
      overflow: hidden;
    }

    @keyframes dropdownSlide {
      from { opacity: 0; transform: translateY(-8px); }
      to { opacity: 1; transform: translateY(0); }
    }

    /* === SEARCH === */
    .select2-search {
      position: relative;
      padding: 0.5rem;
      border-bottom: 1px solid var(--dropdown-border);
    }

    .search-input {
      width: 100%;
      padding: 0.625rem 0.75rem;
      padding-left: 2.25rem;
      font-size: 0.9375rem;
      border: 1px solid var(--input-border);
      border-radius: 0.375rem;
      background: var(--input-bg);
      color: var(--input-text);
      outline: none;
      transition: all 150ms ease;
    }

    .search-input:focus {
      border-color: var(--primary-color);
      background: var(--input-bg);
    }

    .search-icon {
      position: absolute;
      left: 1rem;
      top: 50%;
      transform: translateY(-50%);
      font-size: 0.875rem;
    }

    /* === OPTIONS === */
    .select2-options {
      max-height: 240px;
      overflow-y: auto;
    }

    .select2-option {
      display: flex;
      align-items: center;
      gap: 0.5rem;
      padding: 0.75rem 1rem;
      font-size: 0.9375rem;
      color: var(--dropdown-text, var(--text-color));
      cursor: pointer;
      transition: background 100ms ease;
    }

    .select2-option:hover:not(.disabled) {
      background: var(--dropdown-item-hover);
    }

    .select2-option.selected {
      background: var(--dropdown-item-selected);
      color: var(--primary-color);
      font-weight: 500;
    }

    .select2-option.disabled {
      opacity: 0.5;
      cursor: not-allowed;
    }

    .option-icon {
      font-size: 1rem;
    }

    .option-label {
      flex: 1;
    }

    .check-icon {
      color: var(--primary-color);
      font-weight: bold;
    }

    .select2-no-results {
      padding: 1rem;
      text-align: center;
      color: var(--text-color-muted);
      font-size: 0.875rem;
    }

    /* Dark mode handled automatically by CSS variables */
  `]
})
export class Select2Component implements ControlValueAccessor {
  @Input() options: Select2Option[] = [];
  @Input() label = '';
  @Input() placeholder = 'Seleccionar...';
  @Input() disabled = false;
  @Input() searchable = true;
  @Input() multiple = false;
  @Input() width = ''; // Optional: e.g., '200px', '50%', 'auto'
  @Output() valueChange = new EventEmitter<string | number | (string | number)[]>();

  isOpen = signal(false);
  searchTerm = signal('');
  selectedOption = signal<Select2Option | null>(null);
  selectedOptions = signal<Select2Option[]>([]);

  private readonly elementRef = inject(ElementRef);
  private onChange: (value: unknown) => void = () => { /* noop */ };
  private onTouched: () => void = () => { /* noop */ };

  hasValue(): boolean {
    return this.multiple ? this.selectedOptions().length > 0 : this.selectedOption() !== null;
  }

  filteredOptions(): Select2Option[] {
    const term = this.searchTerm().toLowerCase();
    if (!term) return this.options;
    return this.options.filter(o => o.label.toLowerCase().includes(term));
  }

  isSelected(option: Select2Option): boolean {
    if (this.multiple) {
      return this.selectedOptions().some(o => o.value === option.value);
    }
    return this.selectedOption()?.value === option.value;
  }

  toggleDropdown(): void {
    if (!this.disabled) {
      this.isOpen.update(v => !v);
      if (!this.isOpen()) {
        this.searchTerm.set('');
        this.onTouched();
      }
    }
  }

  selectOption(option: Select2Option): void {
    if (this.multiple) {
      const current = this.selectedOptions();
      if (this.isSelected(option)) {
        this.selectedOptions.set(current.filter(o => o.value !== option.value));
      } else {
        this.selectedOptions.set([...current, option]);
      }
      const values = this.selectedOptions().map(o => o.value);
      this.onChange(values);
      this.valueChange.emit(values);
    } else {
      this.selectedOption.set(option);
      this.onChange(option.value);
      this.valueChange.emit(option.value);
      this.isOpen.set(false);
      this.searchTerm.set('');
    }
  }

  removeTag(option: Select2Option, event: Event): void {
    event.stopPropagation();
    const current = this.selectedOptions();
    this.selectedOptions.set(current.filter(o => o.value !== option.value));
    const values = this.selectedOptions().map(o => o.value);
    this.onChange(values);
    this.valueChange.emit(values);
  }

  @HostListener('document:click', ['$event'])
  onClickOutside(event: Event): void {
    if (!this.elementRef.nativeElement.contains(event.target)) {
      if (this.isOpen()) {
        this.isOpen.set(false);
        this.searchTerm.set('');
      }
    }
  }

  writeValue(value: unknown): void {
    if (this.multiple) {
      const values = value as (string | number)[];
      if (Array.isArray(values)) {
        this.selectedOptions.set(this.options.filter(o => values.includes(o.value)));
      }
    } else {
      const option = this.options.find(o => o.value === value);
      this.selectedOption.set(option || null);
    }
  }

  registerOnChange(fn: (value: unknown) => void): void {
    this.onChange = fn;
  }

  registerOnTouched(fn: () => void): void {
    this.onTouched = fn;
  }

  setDisabledState(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }
}

import { Component, Input, forwardRef } from '@angular/core';
import { CommonModule } from '@angular/common';
import { ControlValueAccessor, NG_VALUE_ACCESSOR } from '@angular/forms';

export interface SelectOption {
  value: string | number;
  label: string;
}

@Component({
  selector: 'app-select',
  standalone: true,
  imports: [CommonModule],
  providers: [
    {
      provide: NG_VALUE_ACCESSOR,
      useExisting: forwardRef(() => SelectComponent),
      multi: true
    }
  ],
  template: `
    <div class="form-group" [class.has-error]="error" [class.disabled]="disabled">
      @if (label) {
        <label class="form-label">{{ label }}</label>
      }
      <div class="select-container">
        <select
          class="form-select"
          [disabled]="disabled"
          [value]="value"
          (change)="onSelectChange($event)"
          (blur)="onTouched()"
        >
          @if (placeholder) {
            <option value="" disabled [selected]="!value">{{ placeholder }}</option>
          }
          @for (option of options; track option.value) {
            <option [value]="option.value">{{ option.label }}</option>
          }
        </select>
        <!-- Arrow handled by form-select background-image -->
      </div>
      @if (error) {
        <span class="select-error">{{ error }}</span>
      }
    </div>
  `,
  styles: [`
    :host {
      display: block;
      width: 100%;
    }

    .select-container {
      position: relative;
      display: flex;
      align-items: center;
      width: 100%;
    }

    .select-arrow {
      position: absolute;
      right: 0.875rem;
      color: var(--text-color-muted, #9ca3af);
      pointer-events: none;
      transition: transform 200ms ease;
    }

    .select-error {
      font-size: 0.75rem;
      color: var(--error-color, #dc2626);
    }
  `]
})
export class SelectComponent implements ControlValueAccessor {
  @Input() options: SelectOption[] = [];
  @Input() label = '';
  @Input() placeholder = '';
  @Input() error = '';
  @Input() disabled = false;

  value: string | number = '';
  onChange: (value: string | number) => void = () => { /* noop */ };
  onTouched: () => void = () => { /* noop */ };

  onSelectChange(event: Event): void {
    const target = event.target as HTMLSelectElement;
    this.value = target.value;
    this.onChange(this.value);
  }

  writeValue(value: string | number): void {
    this.value = value ?? '';
  }

  registerOnChange(fn: (value: string | number) => void): void {
    this.onChange = fn;
  }

  registerOnTouched(fn: () => void): void {
    this.onTouched = fn;
  }

  setDisabledState(isDisabled: boolean): void {
    this.disabled = isDisabled;
  }
}

