    }

    .toast-icon {
      font-size: 1rem;
      font-weight: bold;
      width: 1.5rem;
      height: 1.5rem;
      display: flex;
      align-items: center;
      justify-content: center;
      border-radius: 50%;
      flex-shrink: 0;
