    .accordion-flush ::ng-deep .accordion-item {
      border-radius: 0;
      border-left: none;
      border-right: none;
    }

    .accordion-flush ::ng-deep .accordion-item:first-child {
      border-top: none;
    }
  `]
})
export class AccordionComponent {
  @Input() flush = false;
}

