.data-state {
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: center;
  padding: 2rem;
  min-height: 150px;
}

/* Loading */
.data-state--loading {
  gap: 1rem;
}

.loading-text {
  margin: 0;
  font-size: 0.875rem;
  color: var(--text-color-secondary);
}

/* Error */
.error-container {
  display: flex;
  flex-direction: column;
  align-items: center;
  text-align: center;
  gap: 0.75rem;
  max-width: 400px;
}

.error-icon {
  font-size: 2.5rem;
  color: var(--danger-color, #ef4444);
}

.error-title {
  margin: 0;
  font-size: 1.125rem;
  font-weight: 600;
  color: var(--text-color);
}

.error-message {
  margin: 0;
  font-size: 0.875rem;
  color: var(--text-color-secondary);
  line-height: 1.5;
}

.retry-button {
  display: inline-flex;
  align-items: center;
  gap: 0.5rem;
  padding: 0.5rem 1rem;
  margin-top: 0.5rem;
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--primary-color);
  background: var(--primary-color-lighter);
  border: none;
  border-radius: 0.5rem;
  cursor: pointer;
  transition: all 150ms ease;
}

.retry-button:hover {
  background: var(--primary-color);
  color: white;
}

/* Empty */
.empty-container {
  display: flex;
  flex-direction: column;
  align-items: center;
