    const selected = this.themeService.getSelectedTheme();
    return selected === 'light' ? 'Claro' :
      selected === 'dark' ? 'Oscuro' : 'Sistema';
  });

  themeDescription = computed(() => {
    const isDark = this.themeService.isDarkMode();
