      color: var(--text-color-secondary);
      cursor: pointer;
      transition: all 0.2s ease;
      position: relative;
    }

    .icon-btn:hover {
      background: var(--surface-section);
      color: var(--primary-color);
    }

    .icon-btn:active {
      transform: scale(0.95);
    }

    /* Ghost variant - más sutil */
