    }

    .modal-header {
      display: flex;
      align-items: center;
      justify-content: space-between;
      padding: 1rem 1.25rem;
      border-bottom: 1px solid var(--border-color, #e5e7eb);
    }

    .modal-title {
      font-size: 1.125rem;
      font-weight: 600;
      color: var(--text-color, #111827);
      margin: 0;
    }

    .modal-close {
      width: 2rem;
      height: 2rem;
      display: flex;
      align-items: center;
