import { Component, Input, Output, EventEmitter, signal } from '@angular/core';
import { CommonModule } from '@angular/common';

@Component({
  selector: 'app-rating',
  standalone: true,
  imports: [CommonModule],
  template: `
    <div class="rating" [class]="'rating-' + size" [class.readonly]="readonly">
      @for (star of stars; track $index; let i = $index) {
        <button type="button"
          class="star"
          [class.filled]="i < (hoverValue() ?? value)"
          [class.half]="allowHalf && (i + 0.5) === (hoverValue() ?? value)"
          (mouseenter)="!readonly && onHover(i + 1)"
          (mouseleave)="!readonly && onLeave()"
          (click)="!readonly && onSelect(i + 1)"
          (keydown.enter)="!readonly && onSelect(i + 1)"
          (keydown.space)="!readonly && onSelect(i + 1)"
          [disabled]="readonly"
        >
          <i class="fa-solid fa-star"></i>
        </button>
      }
      @if (showValue) {
        <span class="rating-value">{{ value.toFixed(1) }}</span>
      }
    </div>
  `,
  styles: [`
    .rating {
      display: inline-flex;
      align-items: center;
      gap: 0.125rem;
    }

    .rating-sm .star { width: 1rem; height: 1rem; }
    .rating-md .star { width: 1.5rem; height: 1.5rem; }
    .rating-lg .star { width: 2rem; height: 2rem; }

    .star {
      padding: 0;
      background: none;
      border: none;
      color: var(--border-color, #d1d5db);
      cursor: pointer;
      transition: all 150ms ease;
    }

    .star:hover:not(:disabled) {
      transform: scale(1.1);
    }

    .star.filled {
      color: #fbbf24;
    }

    .star.half {
      position: relative;
      color: var(--border-color, #d1d5db);
    }

    .star.half::before {
      content: '';
      position: absolute;
      left: 0;
      top: 0;
      width: 50%;
      height: 100%;
      overflow: hidden;
      color: #fbbf24;
    }

    .star svg {
      width: 100%;
      height: 100%;
      display: block;
    }

    .readonly .star {
      cursor: default;
    }

    .rating-value {
      margin-left: 0.5rem;
      font-size: 0.875rem;
      font-weight: 500;
      color: var(--text-color-secondary, #6b7280);
    }

    /* Dark mode */
    :host-context(html.dark) .star,
    :host-context([data-theme="dark"]) .star {
      color: var(--border-color, #4b5563);
    }

    :host-context(html.dark) .star.filled,
    :host-context([data-theme="dark"]) .star.filled {
      color: #fcd34d;
    }
  `]
})
export class RatingComponent {
  @Input() value = 0;
  @Input() max = 5;
  @Input() size: 'sm' | 'md' | 'lg' = 'md';
  @Input() readonly = false;
  @Input() allowHalf = false;
  @Input() showValue = false;
  @Output() valueChange = new EventEmitter<number>();

  hoverValue = signal<number | null>(null);

  get stars() {
    return Array(this.max).fill(0);
  }

  onHover(value: number) {
    this.hoverValue.set(value);
  }

  onLeave() {
    this.hoverValue.set(null);
  }

  onSelect(value: number) {
    this.value = value;
    this.valueChange.emit(value);
  }
}

