import {
  Component, Input, Output, EventEmitter, signal, HostListener,
  ElementRef, forwardRef, inject, ChangeDetectionStrategy
} from '@angular/core';
import { CommonModule } from '@angular/common';
import { ControlValueAccessor, NG_VALUE_ACCESSOR, FormsModule } from '@angular/forms';
import { TranslateModule } from '@ngx-translate/core';

export interface Select2Option {
  value: string | number;
  label: string;
  icon?: string;
  disabled?: boolean;
}

@Component({
  // Standalone component for Select2 dropdown
  selector: 'app-select2',
  standalone: true,
  imports: [CommonModule, FormsModule, TranslateModule],
  changeDetection: ChangeDetectionStrategy.OnPush,
  providers: [{
    provide: NG_VALUE_ACCESSOR,
    useExisting: forwardRef(() => Select2Component),
    multi: true
  }],
  template: `
    <div 
      class="select2-wrapper"
      [class.open]="isOpen()"
      [class.disabled]="disabled"
      [class.focused]="isOpen()"
      [class.has-value]="hasValue()"
      [class.multiple]="multiple"
      [class.has-label]="label"
      [style.width]="width || null"
    >
      <div class="select2-trigger" (click)="toggleDropdown()">
        @if (label) {
          <label class="floating-label">{{ label }}</label>
        }
        <!-- Single value display -->
        @if (!multiple) {
          <span class="select2-value">
            @if (selectedOption()) {
              @if (selectedOption()!.icon) {
                <span class="option-icon">{{ selectedOption()!.icon }}</span>
              }
              {{ selectedOption()!.label | translate }}
            } @else if (!label) {
              <span class="placeholder">{{ placeholder | translate }}</span>
            }
          </span>
        }
        
        <!-- Multiple values as tags -->
        @if (multiple) {
          <div class="select2-tags">
            @for (opt of selectedOptions(); track opt.value) {
              <span class="select2-tag">
                {{ opt.label | translate }}
                <button type="button" class="tag-remove" (click)="removeTag(opt, $event)">×</button>
              </span>
            }
            @if (selectedOptions().length === 0 && !label) {
              <span class="placeholder">{{ placeholder | translate }}</span>
            }
          </div>
        }
        
        <span class="select2-arrow">
          <svg width="12" height="12" viewBox="0 0 12 12" fill="none">
            <path d="M2 4L6 8L10 4" stroke="currentColor" stroke-width="2" stroke-linecap="round"/>
          </svg>
        </span>
      </div>

      @if (isOpen()) {
        <div class="select2-dropdown">
          <!-- Search box -->
          @if (searchable) {
            <div class="select2-search">
              <input 
                type="text" 
                class="search-input" 
                placeholder="Buscar..."
                [ngModel]="searchTerm()"
                (ngModelChange)="searchTerm.set($event)"
                (click)="$event.stopPropagation()"
              />
              <span class="search-icon">🔍</span>
            </div>
          }
          
          <!-- Options list -->
          <div class="select2-options">
            @for (option of filteredOptions(); track option.value) {
              <div 
                class="select2-option"
                [class.selected]="isSelected(option)"
                [class.disabled]="option.disabled"
                (click)="!option.disabled && selectOption(option)"
              >
                @if (option.icon) {
                  <span class="option-icon">{{ option.icon }}</span>
                }
                <span class="option-label">{{ option.label | translate }}</span>
                @if (isSelected(option)) {
                  <span class="check-icon">✓</span>
                }
              </div>
            } @empty {
              <div class="select2-no-results">No hay resultados</div>
            }
          </div>
        </div>
      }
    </div>
  `,
  styles: [`
    :host {
      display: block;
      width: 100%;
    }

    .select2-wrapper {
      position: relative;
      width: 100%;
      min-width: 15rem; /* 240px minimum */
    }

    .select2-wrapper.disabled {
      opacity: 0.6;
      pointer-events: none;
    }

    .select2-wrapper.has-label {
      margin-top: 0.75rem;
    }

    /* === TRIGGER === */
    .select2-trigger {
      position: relative;
      display: flex;
      align-items: center;
      height: var(--control-height);
      padding: 0.25rem 0.875rem;
      padding-right: 2.75rem;
      background: var(--input-bg);
      border: 1px solid var(--input-border);
      border-radius: 0.5rem;
      cursor: pointer;
      transition: all 200ms ease;
      font-size: 0.875rem;
      box-sizing: border-box; 
    }

    .select2-wrapper.has-label .select2-trigger {
      padding: 0.25rem 0.875rem; /* Keep centered, remove top padding shift */
    }

    /* === FLOATING LABEL === */
    .floating-label {
      position: absolute;
      left: 0.875rem;
      top: 50%;
      transform: translateY(-50%);
      font-size: 0.875rem;
      color: var(--input-placeholder);
      pointer-events: none;
      transition: all 200ms cubic-bezier(0.4, 0, 0.2, 1);
      background: var(--input-bg);
      padding: 0 0.5rem;
      white-space: nowrap;
    }

    .select2-wrapper.focused .floating-label,
    .select2-wrapper.has-value .floating-label {
      top: -0.625rem;
      transform: translateY(0);
      font-size: 0.8125rem;
      font-weight: 500;
      color: var(--primary-color);
    }

    /* === MULTI-SELECT FIX === */
    .select2-wrapper.multiple .select2-trigger {
      min-height: var(--control-height); /* grows with content */
      height: auto;
      padding: 0.25rem 0.875rem;
      padding-right: 2.75rem;
      align-items: center; /* Center placeholder and tags vertically */
    }

    .select2-wrapper.focused .select2-trigger,
    .select2-trigger:hover {
      border-color: var(--input-border-focus);
    }

    .select2-wrapper.focused .select2-trigger {
      box-shadow: 0 0 0 3px var(--hover-background);
    }

    .select2-value {
      display: flex;
      align-items: center;
      gap: 0.5rem;
      flex: 1;
      font-size: 0.875rem;
      color: var(--input-text);
      line-height: normal; /* Prevent height expansion */
    }

    .placeholder {
      color: var(--input-placeholder);
    }

    .select2-arrow {
      position: absolute;
      right: 0.75rem;
      top: 50%;
      transform: translateY(-50%);
      color: var(--text-color-secondary);
      transition: transform 200ms ease;
    }

    .select2-wrapper.open .select2-arrow {
      transform: translateY(-50%) rotate(180deg);
    }

    /* === TAGS (Multiple) === */
    .select2-tags {
      display: flex;
      flex-wrap: wrap;
      gap: 0.375rem;
      flex: 1;
      padding: 0.125rem 0;
    }

    .select2-tag {
      display: inline-flex;
      align-items: center;
      gap: 0.25rem;
      padding: 0.25rem 0.5rem;
      background: var(--primary-color-lighter);
      color: var(--primary-color);
      border-radius: 9999px;
      font-size: 0.8125rem;
      font-weight: 500;
    }

    .tag-remove {
      display: flex;
      align-items: center;
      justify-content: center;
      width: 1rem;
      height: 1rem;
      padding: 0;
