}

.card:hover {
  box-shadow: var(--shadow-md);
}

.card-elevated {
  background: var(--surface-background);
  box-shadow: var(--shadow-sm);
}

.card-success {
  border-color: var(--success-color);
  background: linear-gradient(135deg, var(--surface-background) 0%, var(--success-color-lighter) 100%);
}

.card-header {
  display: flex;
  align-items: center;
  gap: 0.75rem;
  margin-bottom: 0.75rem;
}

.card-icon {
  font-size: 1.5rem;
  width: 2.5rem;
  height: 2.5rem;
  display: flex;
  align-items: center;
  justify-content: center;
  background: linear-gradient(135deg, var(--primary-color-lighter), var(--secondary-color-lighter));
  border-radius: 0.5rem;
}

.card-title {
  font-size: 1rem;
  font-weight: 600;
  color: var(--text-color);
  margin: 0;
}

.card-content {
  font-size: 0.875rem;
  color: var(--text-color-secondary);
  margin: 0 0 1rem 0;
  line-height: 1.5;
}

.card-footer {
  display: flex;
  align-items: center;
  justify-content: flex-end;
}



/* ========== ALERTS ========== */
.alert {
  display: flex;
  align-items: flex-start;
  gap: 0.75rem;
  padding: 1rem;
  border-radius: 0.5rem;
  margin-bottom: 0.75rem;
}

.alert-icon {
  font-size: 1.25rem;
  flex-shrink: 0;
}

.alert-content {
  font-size: 0.875rem;
  line-height: 1.5;
}

.alert-info {
  background: var(--alert-info-bg);
  border: 1px solid var(--alert-info-border);
  color: var(--alert-info-text);
}

.alert-success {
  background: var(--alert-success-bg);
  border: 1px solid var(--alert-success-border);
  color: var(--alert-success-text);
}

.alert-warning {
  background: var(--alert-warning-bg);
  border: 1px solid var(--alert-warning-border);
  color: var(--alert-warning-text);
}

.alert-danger {
  background: var(--alert-danger-bg);
  border: 1px solid var(--alert-danger-border);
  color: var(--alert-danger-text);
}

/* ========== MODAL ========== */
.modal-overlay {
  position: fixed;
  inset: 0;
  background: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 1000;
  animation: fadeIn 150ms ease;
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

.modal {
  background: var(--surface-background, #ffffff);
  border-radius: 0.75rem;
  width: 90%;
  max-width: 450px;
  box-shadow: 0 25px 50px -12px rgba(0, 0, 0, 0.25);
  animation: slideUp 200ms ease;
}

@keyframes slideUp {
  from { transform: translateY(20px); opacity: 0; }
  to { transform: translateY(0); opacity: 1; }
}

.modal-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 1rem 1.25rem;
  border-bottom: 1px solid var(--border-color, #e5e7eb);
}

.modal-title {
  font-size: 1.125rem;
  font-weight: 600;
  color: var(--text-color);
  margin: 0;
}

.modal-close {
  width: 2rem;
  height: 2rem;
  display: flex;
  align-items: center;
  justify-content: center;
  font-size: 1.5rem;
  color: var(--text-color-secondary);
  background: transparent;
  border: none;
  border-radius: 0.375rem;
  cursor: pointer;
  transition: all 150ms ease;
}

.modal-close:hover {
  background: var(--surface-section);
  color: var(--text-color);
}

.modal-body {
  padding: 1.25rem;
}

.modal-body p {
  margin: 0 0 1rem 0;
  color: var(--text-color-secondary);
  line-height: 1.6;
}

.modal-input-group {
  display: flex;
  flex-direction: column;
  gap: 0.375rem;
}

.modal-footer {
  display: flex;
  justify-content: flex-end;
  gap: 0.75rem;
  padding: 1rem 1.25rem;
  border-top: 1px solid var(--border-color);
}

/* ========== LOADING ========== */
.loading-container {
  display: flex;
  flex-direction: column;
  gap: 1rem;
  /* align-items: flex-start; Removed to allow skeletons to stretch */
}









/* ========== PROGRESS ========== */
.progress-container {
  display: flex;
  align-items: center;
  gap: 1rem;
  margin-bottom: 0.75rem;
}

.progress-bar {
  flex: 1;
  height: 0.5rem;
  background: var(--surface-section);
  border-radius: 9999px;
  overflow: hidden;
}

.progress-fill {
  height: 100%;
  background: var(--primary-color);
  border-radius: 9999px;
  transition: width 300ms ease;
}

.progress-success .progress-fill {
  background: var(--success-color);
}

.progress-label {
  font-size: 0.75rem;
  font-weight: 500;
  color: var(--text-color-secondary);
  min-width: 70px;
}

/* ========== LOADERS ========== */
.loader-grid {
  display: flex;
  flex-wrap: wrap;
  gap: 2rem;
  margin-bottom: 1.5rem;
}

.loader-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.75rem;
  padding: 1.5rem;
  background: var(--surface-section, #f9fafb);
  border-radius: 0.75rem;
  min-width: 100px;
}

.loader-item-gradient {
  background: linear-gradient(135deg, var(--primary-color-lighter, #efe7ef) 0%, var(--secondary-color-lighter, #e3f2fb) 100%);
}

.loader-label {
  font-size: 0.75rem;
  font-weight: 500;
  color: var(--text-color-secondary, #6b7280);
  text-transform: uppercase;
  letter-spacing: 0.05em;
}

.subsection-title {
  font-size: 0.875rem;
  font-weight: 500;
  color: var(--text-color-secondary, #6b7280);
  margin-bottom: 1rem;
}

.loader-sizes {
  display: flex;
  align-items: flex-end;
  gap: 2rem;
}

.loader-size-item {
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 0.5rem;
}

.loader-size-item span {
  font-size: 0.75rem;
  color: var(--text-color-muted, #9ca3af);
}

/* ========== TOOLTIP ========== */
.tooltip-container {
  display: flex;
  gap: 1rem;
}

.tooltip-trigger {
  position: relative;
}

.tooltip-trigger::after {
  content: attr(data-tooltip);
  position: absolute;
  bottom: calc(100% + 8px);
  left: 50%;
  transform: translateX(-50%);
  padding: 0.5rem 0.75rem;
  font-size: 0.75rem;
  background: var(--text-color, #111827);
  color: white;
  border-radius: 0.375rem;
  white-space: nowrap;
  opacity: 0;
  pointer-events: none;
  transition: opacity 150ms ease;
}

.tooltip-trigger:hover::after {
  opacity: 1;
}

/* ========== NUEVOS COMPONENTES ========== */
.avatar-grid {
  display: flex;
  flex-wrap: wrap;
  gap: 1rem;
  align-items: center;
  margin-bottom: 1rem;
}

.chip-grid {
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
}

.stepper-controls {
  display: flex;
  gap: 1rem;
  margin-top: 1.5rem;
  justify-content: center;
}

.skeleton-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: 1.5rem;
}

.skeleton-grid > div {
  display: flex;
  flex-direction: column;
  gap: 0.75rem;
}

.rating-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(150px, 1fr));
  gap: 1.5rem;
}

.rating-grid > div {
  display: flex;
  flex-direction: column;
  gap: 0.5rem;
}

.toggle-grid {
  display: flex;
  flex-wrap: wrap;
  gap: 1.5rem;
  align-items: center;
}

.input-variants-grid {
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: 1.5rem;
  margin-bottom: 1.5rem;
}

/* Utilidades */
.mt-2 { margin-top: 0.5rem; }
.text-sm { font-size: 0.875rem; color: var(--text-color-secondary, #6b7280); }

/* ========== DARK MODE ========== */
:host-context(html.dark),
:host-context([data-theme="dark"]) {
  .showcase-container {
    background: var(--surface-background);
    border-color: var(--border-color);
  }

  .card {
    background: var(--surface-section);
  }

  .card-elevated {
    background: var(--surface-elevated);
  }

  .modal {
    background: var(--surface-section);
  }

  .skeleton {
    background: linear-gradient(90deg, var(--gray-700) 25%, var(--gray-600) 50%, var(--gray-700) 75%);
    background-size: 200% 100%;
  }

  .tooltip-trigger::after {
    background: var(--gray-100);
    color: var(--gray-900);
    box-shadow: var(--shadow-lg);
  }
}

/* Login Preview Overlay */
.login-preview-overlay {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: rgba(0, 0, 0, 0.8);
  backdrop-filter: blur(4px);
  display: flex;
  align-items: center;
  justify-content: center;
  z-index: 9999;
  animation: fadeIn 0.3s ease-out;
}

.login-preview-container {
  width: 100%;
  max-width: 450px;
  animation: slideUp 0.3s ease-out;
}

.close-preview-btn {
  position: absolute;
  top: 1.5rem;
  right: 1.5rem;
  background: rgba(255, 255, 255, 0.1);
  color: white;
  border: 1px solid rgba(255, 255, 255, 0.2);
  padding: 0.75rem 1.5rem;
  border-radius: 0.5rem;
  font-size: 0.875rem;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s ease;
  z-index: 10000;
}

.close-preview-btn:hover {
  background: rgba(255, 255, 255, 0.2);
  border-color: rgba(255, 255, 255, 0.4);
}

@keyframes fadeIn {
  from { opacity: 0; }
  to { opacity: 1; }
}

/* Login Form Styles */
.login-form {
  display: flex;
  flex-direction: column;
  gap: 1rem;
}

.login-options {
  display: flex;
  justify-content: space-between;
  align-items: center;
  font-size: 0.875rem;
  flex-wrap: wrap;
  gap: 0.5rem;
}

.forgot-link,
.register-link {
  color: var(--primary-color);
  text-decoration: none;
  font-weight: 500;
  transition: color 0.15s ease;
}

.forgot-link:hover,
.register-link:hover {
  color: var(--primary-color-hover);
  text-decoration: underline;
}

